/**
 * Test validation of a tampered JWT token.
 * 
 * This test validates the system's ability to detect and reject tampered JWT tokens.
 * The test follows a complete authentication workflow: first registering a new user,
 * then logging in to obtain a valid JWT token, modifying the token signature to 
 * simulate tampering, and finally attempting to validate the modified token.
 * 
 * The test confirms that the authentication system properly verifies token integrity
 * by rejecting the modified token with a 401 Unauthorized error. This ensures that
 * attackers cannot modify tokens to escalate privileges or bypass authentication
 * controls. The signature verification mechanism is critical for maintaining the
 * security of the token-based authentication system.
 * 
 * Step-by-step process:
 * 1. Register a new user account with random credentials
 * 2. Log in with the created credentials to obtain a valid JWT token
 * 3. Extract the JWT token from the authorization header
 * 4. Modify the token signature by changing characters to simulate tampering
 * 5. Attempt to validate the modified token
 * 6. Verify the system rejects it with 401 Unauthorized error
 */
export async function test_api_token_validation_modified_token_failure(
  connection: api.IConnection,
) {
  // 1. Register a new user account with random credentials
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = "password123"; // Use fixed password for consistency

  const registerResponse = await api.functional.auth.user.register.post(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(registerResponse);

  // 2. Log in with the created credentials to obtain a valid JWT token
  // The authentication token is automatically stored in connection.headers.Authorization
  const loginResponse = await api.functional.auth.user.login.post(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ILogin,
  });
  typia.assert(loginResponse);

  // 3. Extract the JWT token from the authorization header
  // The header format is "Bearer <token>"
  const authHeader: string | undefined = connection.headers.Authorization;
  if (!authHeader) {
    throw new Error("Authorization header not found after login");
  }

  const token: string = authHeader.split(" ")[1];
  if (!token) {
    throw new Error("Token not found in Authorization header");
  }

  // 4. Modify the JWT token to simulate tampering
  // Convert to array, modify a few characters in the signature (end of token)
  const tokenArray: string[] = token.split("");

  // Modify a few characters in the signature portion (end of token)
  // Use predictable changes to ensure consistent test behavior
  if (tokenArray.length > 10) {
    // Change the last character
    tokenArray[tokenArray.length - 1] = tokenArray[tokenArray.length - 1] === "a" ? "b" : "a";

    // Change the second-to-last character  
    tokenArray[tokenArray.length - 2] = tokenArray[tokenArray.length - 2] === "x" ? "y" : "x";

    // Change a character near the end
    tokenArray[tokenArray.length - 5] = tokenArray[tokenArray.length - 5] === "m" ? "n" : "m";
  }

  // Join back into a string
  const modifiedToken: string = tokenArray.join("");

  // Ensure the modified token is actually different from the original
  if (modifiedToken === token) {
    throw new Error("Modified token is identical to original token");
  }

  // 5. Attempt to validate the modified (tampered) token
  // This should fail with a 401 Unauthorized error due to signature verification failure
  TestValidator.error("Modified token should be rejected with unauthorized error")(
    () => api.functional.auth.user.validate.post(connection, {
      body: {
        token: modifiedToken
      } satisfies ITokenValidationRequest,
    })
  );
}

