/**
 * Test unauthorized channel deletion prevention.
 * 
 * This test verifies that the system properly enforces authorization rules by 
 * preventing users from deleting channels they don't own. First, a channel is 
 * created by User A. Then, User B attempts to delete User A's channel. The 
 * system should reject this request with a 403 Forbidden error, ensuring that 
 * users can only delete channels they own.
 * 
 * The test follows a complete user journey:
 * 1. Register and authenticate as the first user (User A)
 * 2. Create a channel owned by User A
 * 3. Register and authenticate as a different user (User B)
 * 4. Attempt to delete User A's channel using User B's credentials
 * 5. Verify that the deletion attempt is rejected with 403 Forbidden
 */
export async function test_api_channel_delete_unauthorized_access(
  connection: api.IConnection,
) {
  // Register and authenticate as first user
  const userAEmail = typia.random<string & tags.Format<"email">>();
  const userA = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: userAEmail,
      password: "password123",
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(userA);

  await api.functional.auth.user.login.signIn(connection, {
    body: {
      email: userAEmail,
      password: "password123",
    } satisfies ITodoListUser.ILogin,
  });

  // Create a channel with first user
  const channel = await api.functional.todoList.todoListChannels.create(connection, {
    body: {
      name: "Test Channel",
      description: "Channel created by User A",
    } satisfies ITodoListChannel.ICreate,
  });
  typia.assert(channel);

  // Register and authenticate as second user
  const userBEmail = typia.random<string & tags.Format<"email">>();
  const userB = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: userBEmail,
      password: "password123",
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(userB);

  await api.functional.auth.user.login.signIn(connection, {
    body: {
      email: userBEmail,
      password: "password123",
    } satisfies ITodoListUser.ILogin,
  });

  // Attempt to delete User A's channel with User B's credentials
  // This should fail with 403 Forbidden due to authorization restrictions
  await TestValidator.error("Unauthorized deletion should be rejected")(() => 
    api.functional.todoList.channels.erase(connection, {
      id: channel.id,
    })
  );
}

