    (searchResult.pagination.pages)
    (1);

  TestValidator.equals("Current page should match requested page")
    (searchResult.pagination.current)
    (1);

  TestValidator.equals("Limit should match requested limit")
    (searchResult.pagination.limit)
    (10);
}

