import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { IChangePasswordRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/IChangePasswordRequest";
import type { IChangePasswordResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/IChangePasswordResponse";

/**
 * Test successful password update flow where an authenticated user changes their existing password to a new, stronger one.
 * 
 * This test validates the complete password change workflow from user registration through password update verification. 
 * The test ensures that:
 * 1. A new user can be successfully registered
 * 2. The registered user can authenticate and obtain JWT tokens
 * 3. The authenticated user can successfully change their password with valid credentials
 * 4. The password change operation returns appropriate success response
 * 5. The system properly updates the user's password for future authentication
 * 
 * The test follows a complete user journey that mirrors real-world scenarios where users need to update their passwords 
 * for security reasons or periodic password rotation. It verifies both the API contract compliance and the business logic 
 * of the password change operation.
 */
export async function test_api_password_change_success_valid_credentials(connection: api.IConnection) {
    // 1. Register a new user with valid credentials to establish baseline authentication state
    const email: string = typia.random<string & tags.Format<"email">>();
    const currentPassword: string = "Password123!";

    const user: ITodoListUser = await api.functional.auth.user.register.post(connection, {
        body: {
            email,
            password: currentPassword
        } satisfies ITodoListUser.ICreate
    });
    typia.assert(user);

    // 2. Authenticate the user to obtain JWT tokens for authorized access
    const authenticatedUser: ITodoListUser = await api.functional.auth.user.login.post(connection, {
        body: {
            email,
            password: currentPassword
        } satisfies ITodoListUser.ILogin
    });
    typia.assert(authenticatedUser);

    // 3. Change the password by providing current and new valid passwords
    const newPassword: string = "NewPassword456!";

    const passwordChangeResponse: IChangePasswordResponse = await api.functional.auth.user.password.put(connection, {
        body: {
            current_password: currentPassword,
            new_password: newPassword
        } satisfies IChangePasswordRequest
    });
    typia.assert(passwordChangeResponse);

    // 4. Verify the password change was successful
    TestValidator.equals("Password change should be successful")(passwordChangeResponse.success)(true);

    // 5. Verify that error is thrown when using old password to login
    await TestValidator.error("Login should fail with old password after password change")(async () => {
        await api.functional.auth.user.login.post(connection, {
            body: {
                email,
                password: currentPassword
            } satisfies ITodoListUser.ILogin
        });
    });

    // 6. Verify that login succeeds with new password
    const loggedInWithNewPassword: ITodoListUser = await api.functional.auth.user.login.post(connection, {
        body: {
            email,
            password: newPassword
        } satisfies ITodoListUser.ILogin
    });
    typia.assert(loggedInWithNewPassword);
    TestValidator.equals("Successfully logged in with new password")(loggedInWithNewPassword.id)(user.id);
}

