  TestValidator.equals("Limit should be 10")(
    searchResponse.pagination.limit
  )(10);

  TestValidator.equals("Current page should be 1")(
    searchResponse.pagination.current
  )(1);

  // Verify the response contains exactly 10 channels (default limit)
  TestValidator.equals("Should return 10 channels")(
    searchResponse.data.length
  )(10);

  // Verify total pages is correctly calculated (ceiling of 15/10 = 2)
  TestValidator.equals("Total pages should be 2")(
    searchResponse.pagination.pages
  )(2);

  // Verify all channel summaries have required fields with correct types
  for (const channel of searchResponse.data) {
    typia.assert(channel);

    // Verify required fields exist and are properly formatted
    TestValidator.equals("Channel id should be UUID")(
      typeof channel.id === "string" && channel.id.length > 0
    )(true);

    TestValidator.equals("Channel code should exist")(
      typeof channel.code === "string" && channel.code.length > 0
    )(true);

    TestValidator.equals("Channel name should exist")(
      typeof channel.name === "string" && channel.name.length > 0
    )(true);

    TestValidator.equals("Created at should be valid datetime")(
      typeof channel.created_at === "string" && !isNaN(Date.parse(channel.created_at))
    )(true);
  }
};

