import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test successful creation of a new todo list channel with valid data.
 *
 * This test validates the complete workflow for creating a new channel in the todo list application.
 * The process begins by creating a new user account, authenticating to obtain a JWT token, and validating
 * the token before proceeding. Then, a new channel is created with valid data including name,
 * description, color, and icon properties.
 *
 * The test verifies that the response contains all expected channel details with proper system-generated
 * fields (id, code, timestamps) and confirms the owner association is correctly established by
 * verifying the todo_list_user_id matches the authenticated user.
 *
 * This comprehensive test covers the complete business workflow from user registration through
 * channel creation and validation, ensuring all components work together correctly.
 *
 * @security Requires authentication - user must be logged in to create channels
 * @coverage Full happy path testing with valid input data
 * @scenario User journey from registration to channel creation
 */
export async function test_api_create_channel_with_valid_data(connection: api.IConnection) {
  // 1. Generate unique email and password for new user
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = "password123";

  // 2. Register new user
  const user: ITodoListUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ICreate
  });
  typia.assert(user);

  // 3. Login to obtain authentication token
  const loginResponse: ITodoListUser = await api.functional.auth.user.login.signIn(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ILogin
  });
  typia.assert(loginResponse);

  // 4. Validate authentication token
  const validationResponse: ITokenValidationResponse = await api.functional.auth.user.validate.validateToken(connection, {
    body: {
      token: connection.headers.Authorization!.replace("Bearer ", "")
    } satisfies ITokenValidationRequest
  });
  typia.assert(validationResponse);
  TestValidator.equals("Token should be valid")(validationResponse.valid)(true);
  if (validationResponse.user) {
    TestValidator.equals("User in token should match")(validationResponse.user.id)(user.id);
  }

  // 5. Create new todo list channel with valid data
  const channelName: string = RandomGenerator.paragraph()();
  const channelDescription: string = RandomGenerator.content()()();
  const channelColor: string = `#${RandomGenerator.alphaNumeric(6)}`;
  const channelIcon: string = typia.random<"checklist" | "calendar" | "star" | "task" | "list">();

  const channel: ITodoListChannel = await api.functional.todoList.todoListChannels.create(connection, {
    body: {
      name: channelName,
      description: channelDescription,
      color: channelColor,
      icon: channelIcon
    } satisfies ITodoListChannel.ICreate
  });
  typia.assert(channel);

  // 6. Verify response contains all expected channel properties
  TestValidator.equals("Channel name should match")(channel.name)(channelName);
  TestValidator.equals("Channel description should match")(channel.description)(channelDescription);
  TestValidator.equals("Channel color should match")(channel.color)(channelColor);
  TestValidator.equals("Channel icon should match")(channel.icon)(channelIcon);

  // 7. Confirm owner association is correctly established
  TestValidator.equals("Channel owner should match user")(channel.todo_list_user_id)(user.id);

  // Verify system-generated fields
  TestValidator.predicate("Channel id should be a valid UUID")(typeof channel.id === "string" && channel.id.length > 0);
  TestValidator.predicate("Channel code should be a valid string")(typeof channel.code === "string" && channel.code.length > 0);
  TestValidator.predicate("Created at should be a valid ISO date-time")(!isNaN(Date.parse(channel.created_at)));
  TestValidator.predicate("Updated at should be a valid ISO date-time")(!isNaN(Date.parse(channel.updated_at)));
}

