import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

/**
 * Test user registration with invalid email format.
 * 
 * This test validates that the user registration endpoint properly enforces email format validation
 * rules by rejecting attempts to register with improperly formatted email addresses.
 * The test specifically targets the case where the email is missing the @ symbol,
 * which is a fundamental requirement for valid email addresses.
 * 
 * The test workflow:
 * 1. Create registration data with an invalid email address (missing @ symbol)
 * 2. Attempt to register a new user with the invalid email
 * 3. Verify the system returns a 400 Bad Request error
 * 4. Confirm the error response contains appropriate validation messages
 *    indicating the email format is invalid
 * 
 * This validation is critical for maintaining data quality and security,
 * preventing users from registering with malformed email addresses that
 * could cause issues with authentication, communication, and account recovery.
 */
export const test_api_user_registration_invalid_email_format = async (
  connection: api.IConnection,
) => {
  // Create registration data with invalid email format (missing @ symbol)
  // This violates the fundamental structure of email addresses
  const invalidEmail = "userdomain.com"; // Missing @ symbol

  // Attempt to register user with invalid email format
  // This should trigger input validation and return a 400 error
  await TestValidator.error(
    "User registration with invalid email format should fail", 
    () => api.functional.auth.user.register.post(connection, {
      body: {
        email: invalidEmail,
        password: "strong_password_123!",
      } satisfies ITodoListUser.ICreate,
    }),
  );
};

