  // 3. Create new configuration with valid data
  const config: ITodoListConfiguration = await api.functional.todoList.todoListConfigurations.create(connection, {
    body: {
      name: "max_daily_tasks",
      value: "10",
      description: "Maximum number of tasks allowed per day"
    } satisfies ITodoListConfiguration.ICreate,
  });
  typia.assert(config);

  // 4. Verify response contains all expected fields
  TestValidator.equals("Configuration should have an ID")(!!config.id)(true);
  TestValidator.equals("Configuration should have created_at timestamp")(!!config.created_at)(true);
  TestValidator.equals("Configuration should have updated_at timestamp")(!!config.updated_at)(true);

  // 5. Verify the created configuration has the expected values
  TestValidator.equals("Configuration name should match")(config.name)("max_daily_tasks");
  TestValidator.equals("Configuration value should match")(config.value)("10");
  TestValidator.equals("Configuration description should match")(config.description)("Maximum number of tasks allowed per day");
};

