    email: typia.random<string & tags.Format<"email">>(),
    password: "P@ssw0rd123!"
  } satisfies ITodoListUser.ICreate;

  // Register a new user with valid data
  const output = await api.functional.auth.user.register.post(connection, {
    body: userData
