  // Verify the response contains all required user fields
  TestValidator.equals("User ID is present and valid")(typeof output.id === "string" && output.id.length > 0)(true);
  TestValidator.equals("Created timestamp is present")(typeof output.created_at === "string" && output.created_at.length > 0)(true);
  TestValidator.equals("Updated timestamp is present")(typeof output.updated_at === "string" && output.updated_at.length > 0)(true);
