import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Tests successful validation of a valid JWT token by registering a user,
 * logging in to obtain a token, and validating that token.
 * 
 * This test validates the complete authentication flow:
 * 1. User registration to create a new account
 * 2. User login to obtain a valid JWT token
 * 3. Token validation to confirm the system can verify token authenticity
 * 
 * The test ensures the token validation endpoint correctly verifies 
 * token signatures and expiration status, returning proper validation 
 * responses for valid tokens.
 */
export async function test_api_auth_user_validate_validateToken_success(
  connection: api.IConnection
) {
  // Register a new user account to create user data
  const email = typia.random<string & tags.Format<"email">>();
  const password = "Password123!";

  const registeredUser: ITodoListUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ICreate
  });
  typia.assert(registeredUser);

  // Log in to obtain a valid JWT token
  const loggedInUser: ITodoListUser = await api.functional.auth.user.login.signIn(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ILogin
  });
  typia.assert(loggedInUser);

  // Extract token from authorization header
  const token = connection.headers.Authorization?.replace("Bearer ", "");

  // Validate the token using the token validation endpoint
  const validationResponse: ITokenValidationResponse = 
    await api.functional.auth.user.validate.validateToken(connection, {
      body: {
        token
      } satisfies ITokenValidationRequest
    });
  typia.assert(validationResponse);

  // Verify the token is valid
  TestValidator.equals("Token should be valid")(validationResponse.valid)(true);

  // Verify user information is included in response
  TestValidator.equals("User info should be present")(!!validationResponse.user)(true);

  // Verify the returned user matches the registered user
  TestValidator.equals("User ID should match")(validationResponse.user?.id)(registeredUser.id);
}

