import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

/**
 * Test unauthorized access attempt when trying to update another user's profile.
 *
 * This test validates the authorization system by attempting to update another user's profile
 * with a different user's authentication token. The test follows these steps:
 * 1. Register two separate user accounts (userA and userB)
 * 2. Authenticate as userA to obtain an authentication token
 * 3. Attempt to update userB's profile using userA's token
 * 4. Verify the system rejects the request with 403 Forbidden error
 *
 * This confirms proper authorization controls prevent cross-user profile modification,
 * ensuring users can only modify their own profiles and not access or modify other users' data.
 */
export async function test_api_user_update_unauthorized_access_denied(
  connection: api.IConnection,
) {
  // Register the first user (userA)
  const userAEmail = typia.random<string & tags.Format<"email">>();
  const userA = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: userAEmail,
      password: "password123",
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(userA);

  // Register the second user (userB)
  const userBEmail = typia.random<string & tags.Format<"email">>();
  const userB = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: userBEmail,
      password: "password123",
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(userB);

  // Authenticate as userA to obtain authentication token
  await api.functional.auth.user.login.signIn(connection, {
    body: {
      email: userAEmail,
      password: "password123",
    } satisfies ITodoListUser.ILogin,
  });

  // Attempt to update userB's profile using userA's authentication token
  // This should fail with 403 Forbidden error due to unauthorized access
  await TestValidator.error("Unauthorized access should be denied")(
    () =>
      api.functional.todoList.users.update(connection, {
        userId: userB.id,
        body: {},
      }),
    (error) => {
      // Verify the error is a 403 Forbidden
      if (error.status !== 403) {
        throw new Error(`Expected 403 Forbidden, but got ${error.status}`);
      }

      // Verify the error message indicates authorization denial
      if (!error.message.includes("forbidden") && !error.message.includes("unauthorized")) {
        throw new Error(
          `Error message should indicate authorization denial, but got: ${error.message}`
        );
      }
    }
  );
}

