import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { IChangePasswordRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/IChangePasswordRequest";
import type { IChangePasswordResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/IChangePasswordResponse";

/**
 * Test password change failure due to new password not meeting security requirements.
 * 
 * This test validates that the system properly rejects password change requests when the new password
 * does not meet security requirements. The test follows a complete user journey:
 * 1. Register a new user with valid credentials
 * 2. Authenticate the user to obtain JWT tokens for authorization
 * 3. Attempt to change the password with various invalid new passwords
 * 4. Verify the system returns appropriate validation error responses
 * 
 * The test specifically checks for rejection of passwords that are too short, lack complexity,
 * or otherwise violate the system's password policy. This ensures the security requirements
 * are properly enforced during password changes.
 */
export async function test_api_password_change_failure_invalid_new_password(
  connection: api.IConnection,
): Promise<void> {
  // Generate test credentials with valid format
  const email: string = typia.random<string & tags.Format<"email">>();
  const validPassword: string = "ValidPass123!"; // Meets typical complexity requirements

  // 1. Register a new user with valid credentials
  const user: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email,
        password: validPassword,
      } satisfies ITodoListUser.ICreate,
    },
  );
  typia.assert(user);

  // 2. Authenticate the user to obtain JWT tokens
  const authenticatedUser: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email,
        password: validPassword,
      } satisfies ITodoListUser.ILogin,
    },
  );
  typia.assert(authenticatedUser);

  // 3. Test various password policy violations

  // Test case 1: Password too short (less than minimum length)
  await TestValidator.error(
    "Password change should fail when new password is too short"
  )(async () => {
    await api.functional.auth.user.password.changePassword(connection, {
      body: {
        current_password: validPassword,
        new_password: "123", // Too short
      } satisfies IChangePasswordRequest,
    });
  });

  // Test case 2: Password lacks complexity (no uppercase, lowercase, number, special char)
  await TestValidator.error(
    "Password change should fail when new password lacks complexity"
  )(async () => {
    await api.functional.auth.user.password.changePassword(connection, {
      body: {
        current_password: validPassword,
        new_password: "password", // No complexity
      } satisfies IChangePasswordRequest,
    });
  });

  // Test case 3: Only numbers (lacks letters and special characters)
  await TestValidator.error(
    "Password change should fail when new password contains only numbers"
  )(async () => {
    await api.functional.auth.user.password.changePassword(connection, {
      body: {
        current_password: validPassword,
        new_password: "1234567890", // Only numbers
      } satisfies IChangePasswordRequest,
    });
  });

  // Test case 4: Only letters (lacks numbers and special characters)
  await TestValidator.error(
    "Password change should fail when new password contains only letters"
  )(async () => {
    await api.functional.auth.user.password.changePassword(connection, {
      body: {
        current_password: validPassword,
        new_password: "passwordtest", // Only letters
      } satisfies IChangePasswordRequest,
    });
  });

  // Test case 5: Common password patterns
  await TestValidator.error(
    "Password change should fail with commonly used weak passwords"
  )(async () => {
    await api.functional.auth.user.password.changePassword(connection, {
      body: {
        current_password: validPassword,
        new_password: "password123", // Common pattern
      } satisfies IChangePasswordRequest,
    });
  });
} satisfy ApiFunction<"put__auth_user_password">; // Ensure compliance with the API function signature

