import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

/**
 * Test successful user profile update with valid data.
 * 
 * This test validates that a user can successfully update their profile information
 * with valid data through the PUT /todoList/users/{userId} endpoint. The test
 * follows a complete user journey: creating a new account, authenticating, updating
 * profile information, and verifying the changes.
 * 
 * The test ensures that:
 * 1. Users can be registered successfully
 * 2. Authentication works correctly
 * 3. Profile updates are processed properly
 * 4. Updated user information is returned correctly
 * 5. System timestamps are updated appropriately
 * 
 * Business logic validation includes checking that the updated_at timestamp
 * changes after the update operation, confirming the persistence of changes.
 * 
 * Steps:
 * 1. Register a new user with valid credentials
 * 2. Log in with the new credentials to obtain authentication
 * 3. Update the user's profile information
 * 4. Verify the response contains the expected updated data
 */
export async function test_api_user_update_valid_data_success(
  connection: api.IConnection,
) {
  // Create a unique email for the test user
  const userEmail: string = typia.random<string & tags.Format<"email">>();

  // 1. Register a new user account
  const createUser: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email: userEmail,
        password: "P@ssw0rd123",
      } satisfies ITodoListUser.ICreate,
    },
  );
  typia.assert(createUser);

  // 2. Log in with the new credentials to authenticate
  const login: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email: userEmail,
        password: "P@ssw0rd123",
      } satisfies ITodoListUser.ILogin,
    },
  );
  typia.assert(login);

  // 3. Update the user's profile information
  const updatedUser: ITodoListUser = await api.functional.todoList.users.update(
    connection,
    {
      userId: createUser.id,
      body: {} satisfies ITodoListUser.IUpdate,
    },
  );
  typia.assert(updatedUser);

  // 4. Verify the response contains the expected updated data
  // Check that the user ID matches the original
  TestValidator.equals("user id matches")(
    updatedUser.id, 
    createUser.id
  );

  // Check that timestamps have been updated
  // The updated_at should be different from created_at for an updated user
  TestValidator.predicate("updated_at is present")(
    updatedUser.updated_at !== undefined && updatedUser.updated_at !== null
  );

  // Verify that the updated timestamp is not the same as created timestamp
  // This confirms the update operation actually modified the record
  TestValidator.predicate("record was actually updated")(
    new Date(updatedUser.updated_at) >= new Date(updatedUser.created_at)
  );
};

