import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { IPageITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageITodoListUser";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Test searching for users with invalid search parameters to verify proper validation.
 * 
 * This test validates that the user search endpoint properly handles invalid input
 * parameters by returning appropriate validation errors. The test follows the complete
 * workflow:
 * 1. Register a new user
 * 2. Login with the registered user to authenticate
 * 3. Attempt searches with various invalid parameters
 * 
 * The test verifies validation for:
 * - Non-numeric page values
 * - Negative page numbers
 * - Limit values exceeding the maximum
 * - Invalid sort_order values
 * 
 * Each invalid parameter combination should result in a 400 Bad Request response
 * with appropriate validation messages.
 */
export async function test_api_user_search_invalid_parameters(connection: api.IConnection) {
  // First, register a user to authenticate the search request
  const registerOutput: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email: typia.random<string & tags.Format<"email">>(),
        password: "test_password_123"
      } satisfies ITodoListUser.ICreate
    }
  );
  typia.assert(registerOutput);

  // Login with the registered user to authorize the search request
  const loginOutput: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email: registerOutput.email,
        password: "test_password_123"
      } satisfies ITodoListUser.ILogin
    }
  );
  typia.assert(loginOutput);

  // Test with non-numeric page value
  await TestValidator.error("invalid page format")(() =>
    api.functional.todoList.users.search(connection, {
      body: {
        page: "abc", // non-numeric value
        limit: 10
      } satisfies ITodoListUser.IRequest
    })
  );

  // Test with negative page number
  await TestValidator.error("negative page")(() =>
    api.functional.todoList.users.search(connection, {
      body: {
        page: -1, // negative value
        limit: 10
      } satisfies ITodoListUser.IRequest
    })
  );

  // Test with limit exceeding maximum
  await TestValidator.error("invalid limit")(() =>
    api.functional.todoList.users.search(connection, {
      body: {
        page: 1,
        limit: 1001 // exceeds maximum 1000
      } satisfies ITodoListUser.IRequest
    })
  );

  // Test with invalid sort_order
  await TestValidator.error("invalid sort_order")(() =>
    api.functional.todoList.users.search(connection, {
      body: {
        page: 1,
        limit: 10,
        sort_order: "middle" // not 'asc' or 'desc'
      } satisfies ITodoListUser.IRequest
    })
  );
}

