import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test token validation after successful login.
 * 
 * This test validates the complete authentication flow by first registering a new user account,
 * then logging in with the registered credentials to obtain JWT tokens, and finally validating
 * the obtained token through the token validation endpoint. The test verifies that the login
 * process generates properly structured and signed tokens that can be successfully verified
 * by the authentication system.
 * 
 * The test follows this workflow:
 * 1. Register a new user account with a randomly generated email and password
 * 2. Log in with the registered credentials to obtain authentication tokens
 * 3. Extract the authentication token from the connection headers
 * 4. Validate the token using the token validation endpoint
 * 5. Verify that the token is valid and associated with the correct user
 */
export async function test_api_user_login_token_validation_after_login(
  connection: api.IConnection,
) {
  // Generate random email and password for the new user
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = "password123";

  // 1. Register a new user account
  const registeredUser: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email,
        password
      }
    }
  );
  typia.assert(registeredUser);

  // 2. Log in with the registered credentials
  const loggedInUser: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email,
        password
      }
    }
  );
  typia.assert(loggedInUser);

  // Verify the logged-in user matches the registered user
  TestValidator.equals("user ID matches")(
    registeredUser.id
  )(
    loggedInUser.id
  );

  // Extract the authorization token from the connection
  // The token is automatically stored in the connection headers after login
  const token: string = connection.headers.Authorization.split(" ")[1];

  // 3. Validate the token using the token validation endpoint
  const validationResponse: ITokenValidationResponse = 
    await api.functional.auth.user.validate.validateToken(
      connection,
      {
        body: {
          token
        }
      }
    );
  typia.assert(validationResponse);

  // 4. Verify that the token is valid
  TestValidator.equals("token should be valid")(
    true
  )(
    validationResponse.valid
  );

  // 5. Verify that the validated user matches the registered user
  TestValidator.equals("validated user ID matches")(
    registeredUser.id
  )(
    validationResponse.user?.id
  );
}

