import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test user account deletion with token validation check.
 * 
 * This test verifies that when a user account is deleted, all associated 
 * authentication tokens are properly invalidated. The workflow involves:
 * 1. Registering a new user account with valid credentials
 * 2. Authenticating the user to obtain a JWT token
 * 3. Validating the token is active before account deletion
 * 4. Deleting the user account via the user erase endpoint
 * 5. Attempting to validate the same token again after deletion
 * 6. Confirming the token is no longer valid after user deletion
 * 
 * This ensures the security requirement that deleted user accounts cannot 
 * access the system using previously issued authentication tokens.
 */
export const test_api_user_delete_token_validation = async (
  connection: api.IConnection,
): Promise<void> => {
  // Generate test data for user registration
  const email: string = typia.random<string & typia.tags.Format<"email">>();
  const password: string = "testpassword123";

  // 1. Register a new user account and validate the response
  const user: api.Primitive<api.functional.auth.user.register.signUp.Response> = 
    await api.functional.auth.user.register.signUp(connection, {
      body: {
        email,
        password
      } satisfies api.functional.auth.user.register.signUp.Body
  });
  typia.assert(user);

  // 2. Authenticate the user to obtain JWT token and validate response
  const authenticated: api.Primitive<api.functional.auth.user.login.signIn.Response> = 
    await api.functional.auth.user.login.signIn(connection, {
      body: {
        email,
        password
      } satisfies api.functional.auth.user.login.signIn.Body
  });
  typia.assert(authenticated);

  // Verify authorization token is present in headers
  TestValidator.predicate("Authorization token should be present")(
    !!connection.headers.Authorization
  );

  // 3. Validate the user's authentication token is valid before deletion
  const firstValidation: api.Primitive<api.functional.auth.user.validate.validateToken.Response> = 
    await api.functional.auth.user.validate.validateToken(connection, {
      body: {
        token: connection.headers.Authorization ?? ""
      } satisfies api.functional.auth.user.validate.validateToken.Body
  });
  typia.assert(firstValidation);

  // Verify token is valid before user deletion
  TestValidator.equals("Token should be valid before deletion")
    (firstValidation.valid)
    (true);

  // Verify user ID in token matches created user
  TestValidator.equals("User ID in token should match created user")
    (firstValidation.user?.id)
    (user.id);

  // Ensure the user has not been deleted yet
  TestValidator.equals("User should not be deleted before account deletion")
    (user.deleted_at)
    (null);

  // 4. Delete the user account using the userId from registration
  await api.functional.todoList.users.erase(connection, {
    userId: user.id
  });

  // 5. Create fresh connection to test token invalidation (avoid SDK token refresh)
  // This ensures we're testing the actual token state on the server
  const freshConnection: api.IConnection = {
    ...connection,
    headers: {
      ...connection.headers,
      Authorization: connection.headers.Authorization
    }
  };

  // 6. Attempt to validate the same token again - it should now be invalid
  const secondValidation: api.Primitive<api.functional.auth.user.validate.validateToken.Response> = 
    await api.functional.auth.user.validate.validateToken(freshConnection, {
      body: {
        token: freshConnection.headers.Authorization ?? ""
      } satisfies api.functional.auth.user.validate.validateToken.Body
  });
  typia.assert(secondValidation);

  // Verify token is invalid after user deletion
  TestValidator.equals("Token should be invalid after user deletion")
    (secondValidation.valid)
    (false);
}

