import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test error response when attempting to delete a channel with a non-existent ID.
 *
 * This test validates the API's error handling when attempting to delete a todo list channel
 * that doesn't exist. The test follows a complete user journey:
 * 1. Create a new user account
 * 2. Authenticate the user to obtain JWT token
 * 3. Validate the authentication token
 * 4. Attempt to delete a channel using a valid UUID format that doesn't correspond to any existing channel
 * 5. Verify the API returns an appropriate error response
 *
 * The test ensures the system properly handles invalid resource IDs and returns
 * meaningful error responses to clients, preventing potential security issues
 * from exposing implementation details while still providing adequate feedback.
 */
export async function test_api_delete_channel_not_found(
  connection: api.IConnection,
) {
  // Create a user account for testing
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = "test_password_123";

  const user: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ICreate
    }
  );
  typia.assert(user);

  // Authenticate the created user to obtain JWT token
  const authenticatedUser: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ILogin
    }
  );
  typia.assert(authenticatedUser);

  // Verify the authentication token is valid
  const validationResponse: ITokenValidationResponse = 
    await api.functional.auth.user.validate.validateToken(
      connection,
      {
        body: {
          token: connection.headers.Authorization?.split(" ")[1] ?? ""
        } satisfies ITokenValidationRequest
      }
    );
  typia.assert(validationResponse);
  TestValidator.equals("Token should be valid")(
    validationResponse.valid
  )(true);

  // Attempt to delete a channel with a non-existent ID (valid UUID format)
  // Using a random UUID that doesn't correspond to any existing channel
  const nonExistentChannelId: string = typia.random<string & tags.Format<"uuid">>();

  // Verify that attempting to delete a non-existent channel returns an error
  TestValidator.error("Deleting non-existent channel should fail")(
    async () => {
      await api.functional.todoList.todoListChannels.erase(
        connection,
        {
          id: nonExistentChannelId
        }
      );
    }
  );
}

