import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListConfiguration } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListConfiguration";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

/**
 * Test retrieving all todo list configurations.
 *
 * This test validates the ability to retrieve all todo list configurations
 * through the GET /todoList/todoListConfigurations endpoint. The test follows
 * a complete end-to-end workflow:
 * 1. Register an admin user account to establish authentication context
 * 2. Log in as the admin user to obtain authentication tokens
 * 3. Create multiple configurations to have known data for retrieval
 * 4. Retrieve all configurations
 * 5. Verify the response is an array containing all created items
 * 
 * The test ensures that the index endpoint correctly returns all configurations
 * with proper authentication handling, data integrity, and array structure.
 */
export async function test_api_todo_list_configurations_retrieval_all_success(
  connection: api.IConnection,
) {
  // 1. Register an admin user to establish authentication context
  const adminEmail: string = typia.random<string & tags.Format<"email">>();
  const adminPassword: string = "P@ssw0rd123!";

  const adminUser: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email: adminEmail,
        password: adminPassword,
      } satisfies ITodoListUser.ICreate,
    },
  );
  typia.assert(adminUser);

  // 2. Log in as the admin user to obtain authentication tokens
  const loggedInUser: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email: adminEmail,
        password: adminPassword,
      } satisfies ITodoListUser.ILogin,
    },
  );
  typia.assert(loggedInUser);

  // 3. Create multiple configurations to have known data for retrieval
  const config1Data: ITodoListConfiguration.ICreate = {
    name: "max_daily_tasks",
    value: "25",
    description: "Maximum number of tasks allowed per day",
  };

  const config2Data: ITodoListConfiguration.ICreate = {
    name: "default_theme",
    value: "dark",
    description: "Default UI theme for new users",
  };

  const config3Data: ITodoListConfiguration.ICreate = {
    name: "auto_save_interval",
    value: "30",
    description: "Auto-save interval in seconds",
  };

  // Create the configurations
  const config1: ITodoListConfiguration = 
    await api.functional.todoList.todoListConfigurations.create(
      connection,
      {
        body: config1Data
      } satisfies {
        body: ITodoListConfiguration.ICreate;
      },
    );
  typia.assert(config1);

  const config2: ITodoListConfiguration = 
    await api.functional.todoList.todoListConfigurations.create(
      connection,
      {
        body: config2Data
      } satisfies {
        body: ITodoListConfiguration.ICreate;
      },
    );
  typia.assert(config2);

  const config3: ITodoListConfiguration = 
    await api.functional.todoList.todoListConfigurations.create(
      connection,
      {
        body: config3Data
      } satisfies {
        body: ITodoListConfiguration.ICreate;
      },
    );
  typia.assert(config3);

  // Verify the created configurations have expected values
  TestValidator.equals("config1 name")(
    config1.name
  )(config1Data.name);
  TestValidator.equals("config2 name")(
    config2.name
  )(config2Data.name);
  TestValidator.equals("config3 name")(
    config3.name
  )(config3Data.name);

  // 4. Retrieve all configurations
  // Note: The API function name should be 'index' for retrieving all items
  const allConfigurations: ITodoListConfiguration[] = 
    await api.functional.todoList.todoListConfigurations.index(
      connection
    );
  typia.assert(allConfigurations);

  // 5. Verify the response is an array containing all created items
  // Verify response is an array
  TestValidator.predicate("response is an array")(
    Array.isArray(allConfigurations)
  );

  // Verify the array contains at least the three configurations we created
  TestValidator.predicate("array has at least 3 items")(
    allConfigurations.length >= 3
  );

  // Find our created configurations in the response
  const foundConfig1 = allConfigurations.find(c => c.id === config1.id);
  const foundConfig2 = allConfigurations.find(c => c.id === config2.id);
  const foundConfig3 = allConfigurations.find(c => c.id === config3.id);

  // Verify all created configurations are present in the response
  TestValidator.predicate("config1 is in response")(
    foundConfig1 !== undefined
  );
  TestValidator.predicate("config2 is in response")(
    foundConfig2 !== undefined
  );
  TestValidator.predicate("config3 is in response")(
    foundConfig3 !== undefined
  );

  // Verify the found configurations have correct data
  if (foundConfig1) {
    TestValidator.equals("found config1 name")(
      foundConfig1.name
    )(config1.name);
    TestValidator.equals("found config1 value")(
      foundConfig1.value
    )(config1.value);
  }

  if (foundConfig2) {
    TestValidator.equals("found config2 name")(
      foundConfig2.name
    )(config2.name);
    TestValidator.equals("found config2 value")(
      foundConfig2.value
    )(config2.value);
  }

  if (foundConfig3) {
    TestValidator.equals("found config3 name")(
      foundConfig3.name
    )(config3.name);
    TestValidator.equals("found config3 value")(
      foundConfig3.value
    )(config3.value);
  }

  // Final comprehensive validation
  typia.assert<ITodoListConfiguration[]>(allConfigurations);
}

