import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListChannel";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test validation of channel update with various invalid data inputs.
 * 
 * This test validates the API's ability to properly handle and reject invalid data
 * when updating a todo list channel. The test follows a complete user journey
 * from user registration through authentication and channel creation, then
 * attempts to update the channel with various invalid data patterns.
 * 
 * The test verifies that the API returns appropriate validation errors for:
 * - Empty channel name
 * - Excessively long channel name
 * - Invalid color format (not a valid hex color code)
 * 
 * For each invalid case, the test confirms that the channel remains unmodified
 * and that appropriate error responses are returned. This ensures data integrity
 * and proper error handling in the API.
 */
export async function test_api_update_channel_with_invalid_data(
  connection: api.IConnection,
) {
  // Create a new user for testing
  const userData = {
    body: {
      email: typia.random<string & tags.Format<"email">>(),
      password: "Test123!"
    } satisfies ITodoListUser.ICreate
  };
  const user = await api.functional.auth.user.register.signUp(connection, userData);
  typia.assert(user);

  // Authenticate the created user
  const authData = {
    body: {
      email: userData.body.email,
      password: "Test123!"
    } satisfies ITodoListUser.ILogin
  };
  const authUser = await api.functional.auth.user.login.signIn(connection, authData);
  typia.assert(authUser);

  // Validate the authentication token
  const validationData = {
    body: {
      token: connection.headers.Authorization ?? ""
    } satisfies ITokenValidationRequest
  };
  const validationResponse = await api.functional.auth.user.validate.validateToken(connection, validationData);
  typia.assert(validationResponse);
  TestValidator.equals("Token should be valid")(
    validationResponse.valid
  )(true);

  // Create a channel to update
  const channelData = {
    body: {
      name: "Test Channel",
      description: "A channel for testing purposes",
      color: "#FF5733",
      icon: "checklist"
    } satisfies ITodoListChannel.ICreate
  };
  const channel = await api.functional.todoList.todoListChannels.create(connection, channelData);
  typia.assert(channel);

  // Store original channel data for comparison
  const originalChannel = { ...channel };

  // Test 1: Update with empty name
  TestValidator.error("Update with empty name should fail with validation error")(() => {
    return api.functional.todoList.todoListChannels.update(connection, {
      id: channel.id,
      body: {
        name: ""
      } satisfies ITodoListChannel.IUpdate
    });
  });

  // Test 2: Update with excessively long name
  TestValidator.error("Update with excessively long name should fail with validation error")(() => {
    return api.functional.todoList.todoListChannels.update(connection, {
      id: channel.id,
      body: {
        name: RandomGenerator.alphaNumeric(100)
      } satisfies ITodoListChannel.IUpdate
    });
  });

  // Test 3: Update with invalid color format
  TestValidator.error("Update with invalid color format should fail with validation error")(() => {
    return api.functional.todoList.todoListChannels.update(connection, {
      id: channel.id,
      body: {
        color: "invalid-color"
      } satisfies ITodoListChannel.IUpdate
    });
  });

  // Verify channel remains unmodified after failed updates
  const retrievedChannel = await api.functional.todoList.todoListChannels.at(connection, {
    id: originalChannel.id
  });
  typia.assert(retrievedChannel);
  TestValidator.equals("Channel should remain unmodified after invalid update attempts")(
    retrievedChannel
  )(originalChannel);
}

