import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListChannel";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

;



;
/**
 * Test unauthorized access protection when attempting to delete another user's todo list channel.
 *
 * Validates that the system properly enforces ownership security by preventing users from
 * deleting channels they don't own. The test follows a complete security workflow:
 * 
 * 1. Create two separate user accounts
 * 2. Authenticate as the first user
 * 3. Create a todo list channel owned by the first user
 * 4. Authenticate as the second (unrelated) user
 * 5. Attempt to delete the first user's channel using its ID
 * 6. Verify the system rejects the unauthorized deletion with appropriate error response
 * 
 * This test ensures the soft deletion endpoint (/todoList/todoListChannels/:id) correctly
 * verifies resource ownership before allowing deletion. The security model requires that
 * users can only delete channels where they are the owner (todo_list_user_id matches
 * the authenticated user's ID).
 *
 * The test validates both the business rule enforcement and proper HTTP error signaling.
 * A successful test confirms that even with valid authentication, users cannot perform
 * privileged operations on resources they don't own, protecting data integrity and
 * preventing unauthorized data destruction.
 */
export async function test_api_delete_channel_unauthorized_access(connection: api.IConnection) {
  // Generate unique emails for test users
  const firstUserEmail: string = typia.random<string & typia.tags.Format<"email">>();
  const secondUserEmail: string = typia.random<string & typia.tags.Format<"email">>();

  // Create first user account
  const firstUser: ITodoListUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: firstUserEmail,
      password: "password1",
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(firstUser);

  // Create second user account 
  const secondUser: ITodoListUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: secondUserEmail,
      password: "password2",
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(secondUser);

  // Authenticate as first user
  await api.functional.auth.user.login.signIn(connection, {
    body: {
      email: firstUserEmail,
      password: "password1",
    } satisfies ITodoListUser.ILogin,
  });

  // Verify authentication token is valid
  const firstUserValidation: ITokenValidationResponse = await api.functional.auth.user.validate.validateToken(connection, {
    body: {
      token: connection.headers.Authorization!.split(" ")[1],
    } satisfies ITokenValidationRequest,
  });
  typia.assert(firstUserValidation);
  TestValidator.equals("first user token should be valid")(firstUserValidation.valid)(true);

  // Create a channel as the first user
  const channel: ITodoListChannel = await api.functional.todoList.todoListChannels.create(connection, {
    body: {
      name: "Test Channel",
      description: "A channel created by the first user for unauthorized access testing",
      color: "#FF6B6B",
      icon: "folder",
    } satisfies ITodoListChannel.ICreate,
  });
  typia.assert(channel);

  // Switch context to second user
  await api.functional.auth.user.login.signIn(connection, {
    body: {
      email: secondUserEmail,
      password: "password2",
    } satisfies ITodoListUser.ILogin,
  });

  // Verify second user's authentication token
  const secondUserValidation: ITokenValidationResponse = await api.functional.auth.user.validate.validateToken(connection, {
    body: {
      token: connection.headers.Authorization!.split(" ")[1],
    } satisfies ITokenValidationRequest,
  });
  typia.assert(secondUserValidation);
  TestValidator.equals("second user token should be valid")(secondUserValidation.valid)(true);

  // Attempt to delete the first user's channel (should fail with 403 Forbidden)
  await TestValidator.error("unauthorized channel deletion should be forbidden")(() =>
    api.functional.todoList.todoListChannels.erase(connection, {
      id: channel.id,
    })
  );

  // Optional: Verify the channel still exists and is not deleted
  const readChannel: ITodoListChannel = await api.functional.todoList.todoListChannels.at(connection, {
    id: channel.id,
  });
  typia.assert(readChannel);
  TestValidator.equals("channel should still exist")(
    readChannel.id
  )(channel.id);
  TestValidator.equals("channel should not be deleted")(
    readChannel.deleted_at
  )(undefined);
}

