import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListChannel";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test validation of invalid ID formats when retrieving a todo list channel.
 *
 * This test verifies the API properly validates channel ID parameters by testing various
 * invalid UUID formats. The test follows a complete user journey: user registration,
 * authentication, token validation, and multiple attempts to access a channel
 * with invalid ID formats.
 *
 * The test validates error handling for the following invalid ID formats:
 * - Empty string
 * - Non-UUID string
 * - Malformed UUID (incorrect length)
 * - UUID with invalid characters
 * - Partial UUID
 *
 * @param connection The API connection instance containing authentication credentials
 */
export async function test_api_get_channel_with_invalid_id_format(
  connection: api.IConnection
) {
  // Create test user with random email
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = "testpassword123";

  // Register new user
  const user: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ICreate
    }
  );
  typia.assert(user);

  // Authenticate user
  const authResult: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ILogin
    }
  );
  typia.assert(authResult);

  // Validate authentication token
  const validation: ITokenValidationResponse = 
    await api.functional.auth.user.validate.validateToken(
      connection,
      {
        body: {
          token: connection.headers.Authorization ?? ""
        } satisfies ITokenValidationRequest
      }
  );
  typia.assert(validation);

  // Test various invalid ID formats

  // Test empty string ID
  await TestValidator.error(
    "API should reject empty channel ID"
  )(() => 
    api.functional.todoList.todoListChannels.at(connection, { id: "" })
  );

  // Test non-UUID string
  await TestValidator.error(
    "API should reject non-UUID string format"
  )(() => 
    api.functional.todoList.todoListChannels.at(connection, { id: "not-a-uuid" })
  );

  // Test malformed UUID with incorrect length
  await TestValidator.error(
    "API should reject UUID with incorrect length"
  )(() => 
    api.functional.todoList.todoListChannels.at(connection, { 
      id: "123e4567-e89b-12d3-a456-42661417400" // 31 characters instead of 32
    })
  );

  // Test UUID with invalid characters
  await TestValidator.error(
    "API should reject UUID with invalid characters"
  )(() => 
    api.functional.todoList.todoListChannels.at(connection, { 
      id: "g23e4567-e89b-12d3-a456-426614174000" // 'g' is invalid in first position
    })
  );

  // Test partial UUID
  await TestValidator.error(
    "API should reject partial UUID"
  )(() => 
    api.functional.todoList.todoListChannels.at(connection, { 
      id: "123e4567-e89b" // incomplete UUID
