import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test validation of invalid ID formats when retrieving a todo list channel.
 *
 * This test verifies the API properly validates channel ID parameters by testing various
 * invalid UUID formats. The test follows a complete user journey: user registration,
 * authentication, token validation, and multiple attempts to access a channel
 * with invalid ID formats.
 *
 * The test validates error handling for the following invalid ID formats:
 * - Empty string
 * - Non-UUID string
 * - Malformed UUID (incorrect length)
 * - UUID with invalid characters
 * - Partial UUID
 *
 * @param connection The API connection instance containing authentication credentials
 */
export async function test_api_get_channel_with_invalid_id_format(
  connection: api.IConnection
) {
  // Create test user with random email
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = "testpassword123";

  // Register new user
  const user: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ICreate
    }
  );
  typia.assert(user);

  // Authenticate user
  const authResult: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ILogin
    }
  );
  typia.assert(authResult);

  // Extract token from Authorization header and validate it
  const authHeader = connection.headers.Authorization;
  if (!authHeader) {
    throw new Error("Authorization header is missing after authentication");
  }

  // Extract the token part (remove 'Bearer ' prefix if present)
  const token = authHeader.startsWith("Bearer ") 
    ? authHeader.substring(7) 
    : authHeader;

  const validation = await api.functional.auth.user.validate.validateToken(
    connection,
    {
      body: {
        token
      } satisfies ITokenValidationRequest
    }
