import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

/**
 * Test account deletion attempt with a non-existent user ID.
 * 
 * This test validates the system's proper handling of deletion requests for non-existent user resources.
 * It ensures that when a validly formatted UUID is provided for a user that does not exist in the system,
 * the API returns a proper 404 Not Found error rather than other error types or silent failures.
 * 
 * The test follows a complete workflow:
 * 1. Create a valid user account to establish authentication credentials
 * 2. Log in with the created user to obtain an authenticated session
 * 3. Generate a valid UUID format that is guaranteed not to exist in the system
 * 4. Attempt to delete a user with the non-existent ID
 * 5. Verify the system returns a 404 Not Found error as expected
 * 
 * This is a critical negative test case that ensures the API properly distinguishes between:
 * - Invalid UUID format errors (400 Bad Request)
 * - Non-existent resource errors (404 Not Found)
 * - Authentication/authorization errors (401/403)
 * 
 * Proper 404 handling is essential for client applications to understand when a resource simply doesn't
 * exist versus when there are permission or format issues.
 */
export async function test_api_user_delete_invalid_user_id_not_found(
  connection: api.IConnection
) {
  // First, create a valid user to establish authentication credentials
  // We need a valid authenticated session to test the authorization boundaries
  const user: ITodoListUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: typia.random<string & tags.Format<"email">>(), // Generate valid random email
      password: "SecurePass123", // Use consistent strong password
    } satisfies ITodoListUser.ICreate,
  });

  // Verify the user was created successfully
  // This ensures our authentication source is valid before testing error conditions
  typia.assert(user);
  TestValidator.equals("Created user should have ID")(!!user.id)(true);

  // Log in with the created user to get authentication credentials
  // This establishes our authenticated session for the delete attempt
  const loggedInUser: ITodoListUser = await api.functional.auth.user.login.signIn(connection, {
    body: {
      email: user.email, // Use the same email from registration
      password: "SecurePass123", // Use the same password from registration
    } satisfies ITodoListUser.ILogin,
  });

  // Verify login was successful
  // Confirms our authentication token is now active and valid
  typia.assert(loggedInUser);
  TestValidator.equals("Logged in user matches created user")(loggedInUser.id)(user.id);

  // Generate a valid UUID format that does not exist in the system
  // Using a random UUID ensures it won't match any existing users
  // This tests proper handling of valid format but non-existent resource
  const nonExistentUserId: string = typia.random<string & tags.Format<"uuid">>();

  // Verify the generated ID is different from our created user
  // This ensures we're actually testing a non-existent user scenario
  TestValidator.assertNotEquals("Non-existent user ID should differ from created user ID")(nonExistentUserId)(user.id);

  // Attempt to delete a user with valid UUID format but non-existent ID
  // This should fail with 404 Not Found as expected for non-existent resources
  // Using TestValidator.error ensures we catch the specific HTTP 404 error
  await TestValidator.error("Deleting non-existent user should fail with 404 Not Found")(
    async () => {
