import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListChannel";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test validation of channel creation with various invalid data inputs.
 * 
 * First, create a user and authenticate. Then, attempt to create channels with 
 * invalid data such as empty name, excessively long name (over 100 characters), 
 * invalid color format (not hex), and null required fields. Verify the API 
 * returns appropriate validation errors for each case.
 */
export async function test_api_create_channel_with_invalid_data(
    connection: api.IConnection
): Promise<void> {
    // Prepare a new user for testing
    const email: string = typia.random<string & typia.tags.Format<"email">>();
    const password: string = "p@ssw0rd123";

    // Register user account
    const user: ITodoListUser = await api.functional.auth.user.register.signUp(connection, {
        body: {
            email,
            password
        } satisfies ITodoListUser.ICreate
    });
    typia.assert(user);

    // Login user to obtain authentication token
    const login: ITodoListUser = await api.functional.auth.user.login.signIn(connection, {
        body: {
            email,
            password
        } satisfies ITodoListUser.ILogin
    });
    typia.assert(login);

    // Validate authentication token
    const validation: ITokenValidationResponse = 
        await api.functional.auth.user.validate.validateToken(connection, {
            body: {
                token: connection.headers.Authorization!.replace("Bearer ", "")
            } satisfies ITokenValidationRequest
    });
    typia.assert(validation);
    TestValidator.predicate("Token should be valid")(
        validation.valid === true
    );

    // Test case 1: Empty channel name
    await TestValidator.error("Creating channel with empty name should fail")(() =>
        api.functional.todoList.todoListChannels.create(connection, {
            body: {
                name: "",
                description: "Test channel with empty name"
            } satisfies ITodoListChannel.ICreate
        })
    );

    // Test case 2: Excessively long channel name (over 100 characters)
    await TestValidator.error("Creating channel with name over 100 characters should fail")(() =>
        api.functional.todoList.todoListChannels.create(connection, {
            body: {
                name: "x".repeat(101),
                description: "Test channel with very long name"
            } satisfies ITodoListChannel.ICreate
        })
    );

    // Test case 3: Invalid color format (not hex)
    await TestValidator.error("Creating channel with invalid color format should fail")(() =>
        api.functional.todoList.todoListChannels.create(connection, {
            body: {
                name: "Invalid Color Channel",
                description: "Test channel with invalid color format",
                color: "not-a-hex-color"
            } satisfies ITodoListChannel.ICreate
        })
    );

    // Test case 4: Null required fields (name is required)
    // Note: We can't pass null for name in TypeScript due to type safety, 
    // so this primarily tests that the field is required and cannot be omitted
    await TestValidator.error("Creating channel without name should fail")(() =>
        // @ts-ignore - Forcibly testing the validation by attempting to omit required field
        api.functional.todoList.todoListChannels.create(connection, {
            body: {
                description: "Test channel without name"
            } as any /* as unknown as ITodoListChannel.ICreate */
        })
    );
}

