  connection: api.IConnection,
) {
  // 1. Create first user
  const user1Email: string = typia.random<string & tags.Format<"email">>();
  const user1Password: string = "1q2w3e4r!";

  const user1: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email: user1Email,
        password: user1Password,
      } satisfies ITodoListUser.ICreate,
    }
  );
  typia.assert(user1);

  // 2. Authenticate as first user
  await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email: user1Email,
        password: user1Password,
      } satisfies ITodoListUser.ILogin,
    }
  );

  // 3. Validate first user's token
  const validation1: ITokenValidationResponse = 
    await api.functional.auth.user.validate.validateToken(
      connection, 
      {
        body: { token: connection.headers.Authorization! } satisfies ITokenValidationRequest
      }
  );
  typia.assert(validation1);
  TestValidator.equals("user1 token is valid")(validation1.valid)(true);

  // 4. Create channel with first user
  const originalChannel: ITodoListChannel = 
    await api.functional.todoList.todoListChannels.create(
      connection,
      {
        body: {
          name: "User1's Private Channel",
          description: "This channel should only be modifiable by user1",
          color: "#FF5733",
          icon: "checklist",
        } satisfies ITodoListChannel.ICreate,
      }
    );
  typia.assert(originalChannel);

