  TestValidator.error("unauthorized channel update")(\n    () => api.functional.todoList.todoListChannels.update(\n      connection,\n      {\n        id: originalChannel.id,\n        body: {\n          name: "Hacked Name",\n          description: "This should not be updated",\n          color: "#000000",\n          icon: "warning",\n        } satisfies ITodoListChannel.IUpdate,\n      }\n    )\n  );\n}

