import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

/**
 * Test deleting a configuration with a non-existent ID.
 *
 * This test validates the system's behavior when attempting to delete a todo list configuration
 * using an ID that does not correspond to any existing configuration. The test follows a complete
 * user journey that first establishes the necessary authentication context by registering and
 * logging in as an admin user, which is required for accessing configuration endpoints.
 *
 * The test specifically verifies that the system properly handles invalid deletion attempts
 * by returning an appropriate not-found error response when a valid UUID format is used
 * but no corresponding configuration exists. This prevents accidental data corruption and
 * provides clear feedback to clients about the non-existence of requested resources.
 *
 * The workflow is:
 * 1. Register a new admin user to establish authentication context
 * 2. Log in with the registered user credentials to obtain authentication tokens
 * 3. Attempt to delete a configuration using a randomly generated UUID
 * 4. Verify that the system returns a not-found error as expected
 *
 * This test ensures the API properly validates resource existence before deletion operations,
 * maintaining data integrity and providing appropriate error responses for client applications.
 */
export async function test_api_todo_list_configuration_delete_not_found(
  connection: api.IConnection,
) {
  // Register a new admin user to establish authentication context
  const userData = typia.random<ITodoListUser.ICreate>();
  const registeredUser = await api.functional.auth.user.register.post(connection, {
    body: userData,
  });
  typia.assert(registeredUser);

  // Log in with the registered user to obtain authentication tokens
  await api.functional.auth.user.login.post(connection, {
    body: {
      email: userData.email,
      password: userData.password,
    } satisfies ITodoListUser.ILogin,
  });

  // Attempt to delete a configuration with a non-existent ID
  // Use a valid UUID format that doesn't correspond to any existing configuration
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();

  // Verify that attempting to delete a non-existent configuration returns a not-found error
  await TestValidator.error("Should throw not-found error when deleting non-existent configuration")(
    () => api.functional.todoList.todoListConfigurations.eraseById(connection, {
      id: nonExistentId
    })
  );
};

