import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListChannel";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test error response when attempting to retrieve a channel with a non-existent ID.
 *
 * This test validates that the API properly handles requests for non-existent channels
 * by returning a 404 Not Found response. The test follows a complete user journey:
 * 
 * 1. Create a new user account
 * 2. Authenticate the user to obtain JWT token
 * 3. Validate the authentication token
 * 4. Attempt to retrieve a channel using a valid UUID format that doesn't exist
 * 5. Verify the API returns appropriate error response
 * 
 * This ensures the API correctly handles edge cases where users attempt to access
 * resources that don't exist in the system, providing appropriate feedback rather
 * than exposing internal system details or allowing unauthorized access.
 */
export async function test_api_get_channel_not_found(
  connection: api.IConnection,
) {
  // Create a new user account for testing
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = "P@ssw0rd123!";

  const user: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ICreate
    }
  );
  typia.assert(user);

  // Authenticate the created user to obtain JWT token
  const authenticatedUser: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ILogin
    }
  );
  typia.assert(authenticatedUser);

  // Validate the authentication token
  const validationResponse: ITokenValidationResponse =
    await api.functional.auth.user.validate.validateToken(
      connection,
      {
        body: {
          token: connection.headers["Authorization"]?.replace("Bearer ", "") ?? ""
        } satisfies ITokenValidationRequest
      }
    );
  typia.assert(validationResponse);
  TestValidator.equals("token should be valid")(validationResponse.valid)(true);

  // Generate a valid UUID format that doesn't correspond to any existing channel
  const nonExistentChannelId: string = typia.random<string & tags.Format<"uuid">>();

  // Attempt to retrieve a channel with the non-existent ID and verify 404 response
  await TestValidator.error("Should receive 404 error for non-existent channel")(
    () => api.functional.todoList.todoListChannels.at(connection, {
      id: nonExistentChannelId
    })
  );
}

