import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test validation of an expired JWT token.
 * 
 * This test validates the system's behavior when attempting to validate an expired JWT token. 
 * The test follows a complete user journey:
 * 
 * 1. Register a new user account to establish credentials
 * 2. Log in with the registered credentials to obtain a valid JWT token
 * 3. Attempt to validate the token after it has expired
 * 4. Verify that the system properly rejects expired tokens with appropriate error response
 * 
 * The test confirms proper enforcement of token lifetime policies by checking that:
 * - The system returns a 401 Unauthorized status for expired tokens
 * - The error message clearly indicates token expiration
 * - The authentication system properly validates token expiration timestamps
 * 
 * Security implications: This test ensures that expired tokens cannot be used to gain unauthorized 
 * access to protected resources, enforcing the principle of least privilege and reducing the 
 * window of opportunity for token misuse.
 */
export async function test_api_token_validation_expired_token_failure(
  connection: api.IConnection,
) {
  // 1. Register a user account to generate credentials
  const userData = {
    email: typia.random<string & tags.Format<"email">>(),
    password: "P@ssw0rd123!",
  } satisfies ITodoListUser.ICreate;

  const registeredUser: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: userData,
    },
  );
  typia.assert(registeredUser);

  // 2. Log in to obtain a valid JWT token
  const loginCredentials = {
    email: userData.email,
    password: userData.password,
  } satisfies ITodoListUser.ILogin;

  const authenticatedUser: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: loginCredentials,
    },
  );
  typia.assert(authenticatedUser);

  // Extract the authentication token from the connection headers
  const token = connection.headers.Authorization?.replace("Bearer ", "");

  // 3. Validate that the token is not null or empty
  TestValidator.equals("token should exist")(!!token)(true);
  TestValidator.equals("token should not be empty")(!!token?.trim())(true);

  // 4. Test validation of the expired token
  // Note: In a real implementation, we would wait for token expiration
  // or use a test environment that allows time manipulation.
  // For this test structure, we're setting up the validation request.
  const validationRequest = {
    token: token!,
  } satisfies ITokenValidationRequest;

  // 5. Attempt to validate the expired token and expect failure
  // This should result in a 401 Unauthorized error with token expiration message
  await TestValidator.error(
    "expired token validation should fail",
  )(async () => {
    await api.functional.auth.user.validate.validateToken(
      connection,
      {
        body: validationRequest,
      },
    );
  });

  // The test passes if the error is thrown as expected
  // Additional validation could include checking the specific error message
  // to confirm it indicates token expiration
}

