import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListChannel";
import type { IPageITodoListChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageITodoListChannel";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test error handling when providing invalid search parameters.
 * 
 * This test validates that the API properly handles invalid search parameters
 * when attempting to retrieve todo list channels. The test creates a new user,
 * authenticates them, and then attempts to search channels with various invalid
 * parameter combinations to verify appropriate validation errors are returned.
 * 
 * The test specifically checks for proper handling of:
 * 1. Negative limit values
 * 2. Negative offset values 
 * 3. Excessively large limit values
 * 
 * Each invalid parameter combination should result in a validation error
 * response from the API, preventing the request from processing with invalid
 * pagination parameters. This ensures data integrity and prevents potential
 * performance issues from malformed requests.
 * 
 * The test follows a complete user journey:
 * 1. Create a test user account
 * 2. Authenticate the user to obtain JWT token
 * 3. Validate the authentication token
 * 4. Attempt channel search with negative limit (expect validation error)
 * 5. Attempt channel search with negative offset (expect validation error) 
 * 6. Attempt channel search with excessively large limit (expect validation error)
 */
export async function test_api_search_channels_with_invalid_parameters(
  connection: api.IConnection,
) {
  // Create a new user with random credentials
  const email = typia.random<string & tags.Format<"email">>();
  const password = "password123";

  const user: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection, 
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ICreate
    }
  );
  typia.assert(user);

  // Authenticate the created user
  const authenticatedUser: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ILogin
    }
  );
  typia.assert(authenticatedUser);

  // Validate the authentication token
  const tokenValidation: ITokenValidationResponse = 
    await api.functional.auth.user.validate.validateToken(
      connection,
      {
        body: {
          token: connection.headers.Authorization!.split(' ')[1]!
        } satisfies ITokenValidationRequest
      }
    );
  typia.assert(tokenValidation);
  TestValidator.equals("Token should be valid")(tokenValidation.valid)(true);

  // Test with negative limit
  await TestValidator.error("Should reject negative limit")(() => 
    api.functional.todoList.todoListChannels.patch(
      connection,
      {
        body: {
          limit: -1,
          offset: 0
        } satisfies ITodoListChannel.IRequest
      }
    )
  );

  // Test with negative offset
  await TestValidator.error("Should reject negative offset")(() => 
    api.functional.todoList.todoListChannels.patch(
      connection,
      {
        body: {
          limit: 10,
          offset: -1
        } satisfies ITodoListChannel.IRequest
      }
    )
  );

  // Test with excessively large limit
  await TestValidator.error("Should reject excessively large limit")(() => 
    api.functional.todoList.todoListChannels.patch(
      connection,
      {
        body: {
          limit: 10000,
          offset: 0
        } satisfies ITodoListChannel.IRequest
      }
    )
  );
}

