import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListChannel";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test successful update of an existing channel.
 * 
 * This test validates the complete workflow of updating a channel's properties.
 * It covers the end-to-end process from user registration through authentication,
 * channel creation, channel update, and response validation. The test verifies
 * that all modifiable channel properties (name, description, color, and icon)
 * can be successfully updated and that the system properly tracks modification
 * time through the updated_at timestamp.
 * 
 * The test follows this sequence:
 * 1. Create a user and authenticate to establish ownership context
 * 2. Validate the authentication token to ensure session validity
 * 3. Create a channel with initial properties to have a target for update
 * 4. Update the channel with new values for all properties
 * 5. Verify the response contains the updated information
 * 6. Confirm the updated_at timestamp has been modified to reflect the change
 * 
 * This comprehensive test ensures the channel update functionality works
 * correctly and maintains data integrity throughout the modification process.
 */
export async function test_api_update_channel_success(
  connection: api.IConnection,
) {
  // 1. Create a user for channel ownership and authentication
  const email: string & tags.Format<"email"> = typia.random<string & tags.Format<"email">>();
  const password: string = "password123";

  const user: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email: email,
        password: password
      } satisfies ITodoListUser.ICreate
    }
  );
  typia.assert(user);

  // 2. Authenticate the user to obtain JWT token for protected endpoints
  const authenticatedUser: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email: email,
        password: password
      } satisfies ITodoListUser.ILogin
    }
  );
  typia.assert(authenticatedUser);

  // 3. Validate the authentication token to ensure it's valid
  const validationResponse: ITokenValidationResponse = await api.functional.auth.user.validate.validateToken(
    connection,
    {
      body: {
        token: connection.headers.Authorization?.split(" ")[1] ?? ""
      } satisfies ITokenValidationRequest
    }
  );
  typia.assert(validationResponse);
  TestValidator.equals("Token should be valid")(
    validationResponse.valid
  )(true);

  // 4. Create a channel with initial data to have a target for update
  const initialChannelData: ITodoListChannel.ICreate = {
    name: "Initial Channel Name",
    description: "Initial channel description",
    color: "#FF5733",
    icon: "checklist"
  };

  const channel: ITodoListChannel = await api.functional.todoList.todoListChannels.create(
    connection,
    {
      body: initialChannelData
    }
  );
  typia.assert(channel);

  // Store the original updated_at timestamp for comparison
  const originalUpdatedAt: string & tags.Format<"date-time"> = channel.updated_at;

  // 5. Update the channel with new values for all properties
  const updatedChannelData: ITodoListChannel.IUpdate = {
    name: "Updated Channel Name",
    description: "Updated channel description",
    color: "#33A1FF",
    icon: "calendar"
  };

  const updateResponse: ITodoListChannel.ISummary = await api.functional.todoList.todoListChannels.update(
    connection,
    {
      id: channel.id,
      body: updatedChannelData
    }
  );
  typia.assert(updateResponse);

  // 6. Verify the response contains the expected updated information
  TestValidator.equals("Updated name should match")(
    updateResponse.name
  )(updatedChannelData.name);

  TestValidator.equals("Updated description should match")(
    updateResponse.description
  )(updatedChannelData.description);

  TestValidator.equals("Updated color should match")(
    updateResponse.color
  )(updatedChannelData.color);

  TestValidator.equals("Updated icon should match")(
    updateResponse.icon
  )(updatedChannelData.icon);

  // 7. Verify the updated_at timestamp has been properly modified
  // Parse timestamps to Date objects for comparison
  const originalDate: Date = new Date(originalUpdatedAt);
  const updatedDate: Date = new Date(updateResponse.created_at); // Note: using created_at from response for comparison

  // The updated_at timestamp should be the same as created_at in the summary
  TestValidator.equals("updated_at should equal response created_at")(
    channel.updated_at
  )(updateResponse.created_at);

  // The updated timestamp should be after the original timestamp
  TestValidator.predicate("updated_at should be after original updated_at")(
    updatedDate.getTime() > originalDate.getTime()
  );
}

