import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListChannel";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test successful creation of a new todo list channel with valid data.
 *
 * This test validates the complete workflow for creating a new channel in the todo list application.
 * The process begins by creating a new user account, authenticating to obtain a JWT token, and validating
 * the token before proceeding. Then, a new channel is created with valid data including name,
 * description, color, and icon properties.
 *
 * The test verifies that the response contains all expected channel details with proper system-generated
 * fields (id, code, timestamps) and confirms the owner association is correctly established by
 * verifying the todo_list_user_id matches the authenticated user.
 *
 * This comprehensive test covers the complete business workflow from user registration through
 * channel creation and validation, ensuring all components work together correctly.
 *
 * @security Requires authentication - user must be logged in to create channels
 * @coverage Full happy path testing with valid input data
 * @scenario User journey from registration to channel creation
 */
export async function test_api_create_channel_with_valid_data(connection: api.IConnection) {
  // 1. Generate unique email and password for new user
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = "password123";

  // 2. Register new user
  const user: ITodoListUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ICreate
  });
  typia.assert(user);

  // 3. Login to obtain authentication token
  const loginResponse: ITodoListUser = await api.functional.auth.user.login.signIn(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ILogin
  });
  typia.assert(loginResponse);

