import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListChannel";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { ITokenValidationRequest } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationRequest";
import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test error handling when attempting to create a channel with a name that already exists for the same user.
 *
 * This test validates the system's ability to prevent duplicate channel names for the same user,
 * ensuring data integrity and proper error handling. The test follows a complete user journey:
 * 1. Create a user account for testing
 * 2. Authenticate the created user to obtain JWT token
 * 3. Validate the authentication token
 * 4. Create an initial channel with a specific name
 * 5. Attempt to create another channel with the same name 
 * 6. Verify the system returns an appropriate error response for the duplicate name
 *
 * The test ensures that the constraint validation is working correctly at the API level,
 * preventing users from creating channels with duplicate names which could lead to
 * confusion and data management issues.
 */
export const test_api_create_channel_with_duplicate_name = async (
  connection: api.IConnection,
): Promise<void> => {
  // 1. Create a user account for testing
  const email: string = typia.random<string & typia.tags.Format<"email">>();
  const password: string = "test1234";

  const user: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ICreate,
    },
  );
  typia.assert(user);

  // 2. Authenticate the created user to obtain JWT token
  await api.functional.auth.user.login.signIn(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ILogin,
  });
  typia.assert(user);

  // 3. Validate the authentication token
  const validationResponse: ITokenValidationResponse = 
    await api.functional.auth.user.validate.validateToken(connection, {
      body: {
        token: connection.headers.Authorization ?? ""
      } satisfies ITokenValidationRequest,
    });
  typia.assert(validationResponse);
  TestValidator.equals("Token should be valid")(validationResponse.valid)(true);

  // 4. Create an initial channel with a specific name
  const channelName: string = RandomGenerator.name();

  const firstChannel: ITodoListChannel = 
    await api.functional.todoList.todoListChannels.create(connection, {
      body: {
        name: channelName
      } satisfies ITodoListChannel.ICreate,
    });
  typia.assert(firstChannel);
  TestValidator.equals("Channel name should match")(firstChannel.name)(channelName);

  // 5. Attempt to create another channel with the same name
  // Verify the system returns an appropriate error response indicating the name constraint violation
  await TestValidator.error("Should not allow duplicate channel names")(async () => {
    await api.functional.todoList.todoListChannels.create(connection, {
      body: {
        name: channelName
      } satisfies ITodoListChannel.ICreate,
    });
  });
};

