import type { IChangePasswordResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/IChangePasswordResponse";

/**
 * Test password change failure due to new password not meeting security requirements.
 * 
 * This test validates that the system properly rejects password change requests when the new password
 * does not meet security requirements. The test follows a complete user journey:
 * 1. Register a new user with valid credentials
 * 2. Authenticate the user to obtain JWT tokens for authorization
 * 3. Attempt to change the password with various invalid new passwords
 * 4. Verify the system returns appropriate validation error responses
 * 
 * The test specifically checks for rejection of passwords that are too short, lack complexity,
 * or otherwise violate the system's password policy. This ensures the security requirements
 * are properly enforced during password changes.
 */
export async function test_api_password_change_failure_invalid_new_password(
  connection: api.IConnection,
): Promise<void> {
  // Generate test credentials with valid format
  const email: string = typia.random<string & tags.Format<"email">>();
  const validPassword: string = "ValidPass123!"; // Meets typical complexity requirements

  // 1. Register a new user with valid credentials
  const user = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email,
        password: validPassword,
      } satisfies ITodoListUser.ICreate,
    },
  );
  typia.assert(user);

  // 2. Authenticate the user to obtain JWT tokens
  const authenticatedUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email,
        password: validPassword,
      } satisfies ITodoListUser.ILogin,
    },
  );
  typia.assert(authenticatedUser);

  // 3. Test password policy violations
  // Test with a password that is too short
  await TestValidator.error(
    "Password change should fail when new password is too short"
  )(async () => {
    await api.functional.auth.user.password.changePassword(connection, {
      body: {
        current_password: validPassword,
        new_password: "123", // Too short
      } satisfies IChangePasswordRequest,
    });
  });

  // Test with a password lacking complexity
  await TestValidator.error(
    "Password change should fail when new password lacks complexity"
  )(async () => {
    await api.functional.auth.user.password.changePassword(connection, {
      body: {
        current_password: validPassword,
        new_password: "password", // No complexity
      } satisfies IChangePasswordRequest,
    });
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

/**
 * Test successful user profile update with valid data.
 * 
 * This test validates that a user can successfully update their profile information
 * with valid data through the PUT /todoList/users/{userId} endpoint. The test
 * follows a complete user journey: creating a new account, authenticating, updating
 * profile information, and verifying the changes.
 * 
 * The test ensures that:
 * 1. Users can be registered successfully
 * 2. Authentication works correctly
 * 3. Profile updates are processed properly
 * 4. Updated user information is returned correctly
 * 5. System timestamps are updated appropriately
 * 
 * Business logic validation includes checking that the updated_at timestamp
 * changes after the update operation, confirming the persistence of changes.
 * 
 * Steps:
 * 1. Register a new user with valid credentials
 * 2. Log in with the new credentials to authenticate
 * 3. Update the user's profile information
 * 4. Verify the response contains the expected updated data
 */
export async function test_api_user_update_valid_data_success(
  connection: api.IConnection,
) {
  // Create a unique email for the test user
  const userEmail: string = typia.random<string & tags.Format<"email">>();

  // 1. Register a new user account
  const createUser: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email: userEmail,
        password: "P@ssw0rd123",
      } satisfies ITodoListUser.ICreate,
    },
  );
  typia.assert(createUser);

  // 2. Log in with the new credentials to authenticate
  const login: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email: userEmail,
        password: "P@ssw0rd123",
      } satisfies ITodoListUser.ILogin,
    },
  );
  typia.assert(login);

  // 3. Update the user's profile information
  const updatedUser: ITodoListUser = await api.functional.todoList.users.update(
    connection,
    {
      userId: createUser.id,
      body: {} satisfies ITodoListUser.IUpdate,
    },
  );
  typia.assert(updatedUser);

  // 4. Verify the response contains the expected updated data
  // Check that the user ID matches the original
  TestValidator.equals("user id matches")(updatedUser.id)(createUser.id);

  // Check that timestamps have been updated
  // The updated_at should be different from created_at for an updated user
  TestValidator.predicate("updated_at is present")(
    updatedUser.updated_at !== undefined && updatedUser.updated_at !== null
  );

  // Verify that the updated timestamp is not the same as created timestamp
  // This confirms the update operation actually modified the record
  TestValidator.predicate("record was actually updated")(
    new Date(updatedUser.updated_at) >= new Date(updatedUser.created_at)
  );
};

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

/**
 * Test unauthorized access attempt when trying to update another user's profile.
 *
 * This test validates the authorization system by attempting to update another user's profile
 * with a different user's authentication token. The test follows these steps:
 * 1. Register two separate user accounts (userA and userB)
 * 2. Authenticate as userA to obtain an authentication token
 * 3. Attempt to update userB's profile using userA's token
 * 4. Verify the system rejects the request with 403 Forbidden error
 *
 * This confirms proper authorization controls prevent cross-user profile modification,
 * ensuring users can only modify their own profiles and not access or modify other users' data.
 */
export async function test_api_user_update_unauthorized_access_denied(
  connection: api.IConnection,
) {
  // Register the first user (userA)
  const userAEmail = typia.random<string & tags.Format<"email">>();
  const userA = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: userAEmail,
      password: "password123",
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(userA);

  // Register the second user (userB)
  const userBEmail = typia.random<string & tags.Format<"email">>();
  const userB = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: userBEmail,
      password: "password123",
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(userB);

  // Authenticate as userA to obtain authentication token
  await api.functional.auth.user.login.signIn(connection, {
    body: {
      email: userAEmail,
      password: "password123",
    } satisfies ITodoListUser.ILogin,
  });

  // Attempt to update userB's profile using userA's authentication token
  // This should fail with 403 Forbidden error due to unauthorized access
  await TestValidator.error("Unauthorized access should be denied")(
    () =>
      api.functional.todoList.users.update(connection, {
        userId: userB.id,
        body: {},
      }),
  );
}

