import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

/**
 * Test unauthorized attempt to delete another user\'s account.
 *
 * This test validates the authorization system\'s ability to prevent users from
 * deleting accounts they do not own. The test creates two separate user accounts,
 * authenticates as the first user, and attempts to delete the second user\'s account.
 * The system must reject this request with a 403 Forbidden error, confirming
 * proper access control enforcement.
 *
 * The test follows this workflow:
 * 1. Register the first user account
 * 2. Register the second user account
 * 3. Authenticate as the first user to obtain JWT token
 * 4. Attempt to delete the second user\'s account using first user\'s token
 * 5. Verify the system rejects the request with 403 Forbidden error
 */
export const test_api_user_delete_unauthorized_other_account_denied = async (
  connection: api.IConnection,
) => {
  // Create the first user account that will attempt the unauthorized deletion
  const firstUserEmail = typia.random<string & tags.Format<"email">>();
  const firstUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: firstUserEmail,
      password: "password123",
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(firstUser);

  // Create the second user account whose account will be the target of the unauthorized deletion attempt
  const secondUserEmail = typia.random<string & tags.Format<"email">>();
  const secondUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: secondUserEmail,
      password: "password123",
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(secondUser);

  // Authenticate as the first user to obtain a JWT token that will be used in the unauthorized deletion attempt
  const authenticatedFirstUser = await api.functional.auth.user.login.signIn(connection, {
    body: {
      email: firstUserEmail,
      password: "password123",
    } satisfies ITodoListUser.ILogin,
  });
  typia.assert(authenticatedFirstUser);

  // Attempt to delete the second user\'s account using the first user\'s token and verify that the system rejects the request with a 403 Forbidden error, confirming that users cannot delete accounts they do not own
  await TestValidator.error(
    "Unauthorized user deletion should be rejected with 403 Forbidden",
  )(async () => {
    await api.functional.todoList.users.erase(connection, {
      userId: secondUser.id,
    });
  });
};

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
