 *
 * This test validates the pagination functionality of the channel search API by:
 * 1. Creating a test user account
 * 2. Authenticating the user to obtain JWT token
 * 3. Creating multiple channels (15) to ensure pagination is triggered
 * 4. Searching channels with default pagination parameters (limit=10, offset=0)
 * 5. Verifying the response returns correct pagination metadata
 * 
 * The test verifies that the API properly handles pagination by checking:
 * - The response contains exactly 10 channels (the default limit)
 * - The pagination metadata shows correct total count (15)
 * - The limit is set to 10 as default
 * - The offset is 0 as default
 * - The total pages is correctly calculated (2 pages)
 * - Channel summaries contain all required fields with proper formatting
 */
export const test_api_search_channels_with_pagination = async (
  connection: api.IConnection,
) => {
  // Create a new user account
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = "password123";

  const user: ITodoListUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(user);

  // Authenticate the created user
  const authenticatedUser: ITodoListUser = await api.functional.auth.user.login.signIn(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ILogin,
  });
  typia.assert(authenticatedUser);

  // Get authorization token safely
  const headers = connection.headers ?? {};
  const authorization = (typeof headers.Authorization === "string"
    ? headers.Authorization
    : typeof headers.authorization === "string"
      ? headers.authorization
      : undefined) ?? "";
  const token: string = authorization.replace("Bearer ", "");

  // Verify the authentication token is valid
  const validationResponse: ITokenValidationResponse = await api.functional.auth.user.validate.validateToken(connection, {
    body: {
      token
    } satisfies ITokenValidationRequest,
  });
  typia.assert(validationResponse);
  TestValidator.equals("Token should be valid")(validationResponse.valid)(true);

  // Create 15 channels to ensure pagination is triggered
  const channelPromises = ArrayUtil.repeat(15)(() => 
    api.functional.todoList.todoListChannels.create(connection, {
      body: {
        name: RandomGenerator.paragraph()(),
        description: RandomGenerator.content()()(),
        color: "#" + Math.floor(Math.random()*16777215).toString(16),
        icon: RandomGenerator.pick(["checklist", "calendar", "star", "task"])
      } satisfies ITodoListChannel.ICreate,
    })
  );

  const channels: ITodoListChannel[] = await ArrayUtil.asyncRepeat(channelPromises.length)(
    async (index) => {
      const channel = await channelPromises[index];
      typia.assert(channel);
      return channel;
    }
  );

  // Search channels with default pagination parameters
  const searchResponse: IPageITodoListChannel.ISummary = await api.functional.todoList.todoListChannels.search(connection, {
    body: {}
  });
  typia.assert(searchResponse);

  // Verify pagination metadata
  TestValidator.equals("Total records should be 15")(
    searchResponse.pagination.records
  )(15);

  TestValidator.equals("Limit should be 10")(
    searchResponse.pagination.limit
  )(10);

  TestValidator.equals("Current page should be 1")(
    searchResponse.pagination.current
  )(1);

  // Verify the response contains exactly 10 channels (default limit)
  TestValidator.equals("Should return 10 channels")(
    searchResponse.data.length
  )(10);

  // Verify total pages is correctly calculated (ceiling of 15/10 = 2)
  TestValidator.equals("Total pages should be 2")(
    searchResponse.pagination.pages
  )(2);

  // Verify all channel summaries have required fields with correct types
  for (const channel of searchResponse.data) {
    typia.assert(channel);

    // Verify required fields exist and are properly formatted
    TestValidator.equals("Channel id should be UUID")(
      typeof channel.id === "string" && channel.id.length > 0
    )(true);

    TestValidator.equals("Channel code should exist")(
      typeof channel.code === "string" && channel.code.length > 0
    )(true);

    TestValidator.equals("Channel name should exist")(
      typeof channel.name === "string" && channel.name.length > 0
    )(true);

    TestValidator.equals("Created at should be valid datetime")(
      typeof channel.created_at === "string" && !isNaN(Date.parse(channel.created_at))
    )(true);
  }
};

