import type { ITokenValidationResponse } from "@ORGANIZATION/PROJECT-api/lib/structures/ITokenValidationResponse";

/**
 * Test filtering channels by name with case-insensitive search.
 *
 * This test validates the search functionality for todo list channels by name filter. 
 * The test follows a complete user journey: creating a user account, authenticating, 
 * creating multiple channels with varying names, and searching with a specific keyword 
 * in various case combinations to verify case-insensitive filtering works correctly.
 *
 * The test specifically verifies that the search endpoint correctly filters channels 
 * based on name containing a keyword ("work"), regardless of case, and excludes 
 * channels that don't contain the keyword. This ensures the search functionality 
 * provides accurate results for users looking to find specific channels.
 *
 * Step-by-step process:
 * 1. Create a test user account for authentication
 * 2. Authenticate the created user to obtain a JWT token
 * 3. Validate the authentication token is valid
 * 4. Create multiple channels with varying names, including some with "work" keyword
 * 5. Search channels using case-insensitive "work" keyword filter
 * 6. Verify search results contain only channels with "work" in the name (any case)
 * 7. Verify search results exclude channels without the "work" keyword
 */
export async function test_api_search_channels_by_name_filter(connection: api.IConnection) {
  // 1. Create a test user account
  const userEmail: string = typia.random<string & tags.Format<"email">>();
  const password: string = "password123!";

  const user: ITodoListUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: userEmail,
      password: password
    } satisfies ITodoListUser.ICreate
  });
  typia.assert(user);

  // 2. Authenticate the user to get JWT token
  const authenticatedUser: ITodoListUser = await api.functional.auth.user.login.signIn(connection, {
    body: {
      email: userEmail,
      password: password
    } satisfies ITodoListUser.ILogin
  });
  typia.assert(authenticatedUser);

  // 3. Validate the authentication token
  // Extract token from connection headers with proper null checking
  const authorizationHeader = connection.headers?.Authorization;
  const token = typeof authorizationHeader === "string" ? 
    authorizationHeader.startsWith("Bearer ") ? 
      authorizationHeader.substring(7) : 
      authorizationHeader : 
    "";

  // If no token found, throw an error
  if (!token) {
    throw new Error("No authentication token found in connection headers");
  }

  const validationResponse: ITokenValidationResponse = 
    await api.functional.auth.user.validate.validateToken(connection, {
      body: {
        token: token
      } satisfies ITokenValidationRequest
  });
  typia.assert(validationResponse);
  TestValidator.equals("token should be valid")(validationResponse.valid)(true);

  // 4. Create multiple channels with varying names
  const channels: ITodoListChannel[] = [];

  // Create channels with "work" in various cases
  const workChannelNames = [
    "Work Projects", 
    "work tasks", 
    "WOrK jOBS",
    "After work activities"
  ];

  // Create channels without "work"
  const nonWorkChannelNames = [
    "Personal Tasks",
    "Family Events",
    "Hobby Activities"
  ];

  // Create channels with work-related names (different cases)
  for (const name of workChannelNames) {
    const channel: ITodoListChannel = await api.functional.todoList.todoListChannels.create(connection, {
      body: {
        name: name,
        description: `Channel for ${name}`
      } satisfies ITodoListChannel.ICreate
    });
    typia.assert(channel);
    channels.push(channel);
  }

  // Create channels without work in name
  for (const name of nonWorkChannelNames) {
    const channel: ITodoListChannel = await api.functional.todoList.todoListChannels.create(connection, {
      body: {
        name: name,
        description: `Channel for ${name}`
      } satisfies ITodoListChannel.ICreate
    });
    typia.assert(channel);
    channels.push(channel);
  }

  // 5 & 6. Search for channels with "work" keyword (case-insensitive)
  // Using lowercase filter to test case-insensitive search
  const searchResults: IPageITodoListChannel.ISummary = 
    await api.functional.todoList.todoListChannels.search(connection, {
      body: {
        name: "work"
      } satisfies ITodoListChannel.IRequest
    });
  typia.assert(searchResults);

  // 7. Verify search results contain only channels with "work" in name
  // and exclude channels without "work"

  // Gather all channel names from search results for verification
  const resultChannelNames = searchResults.data.map(channel => channel.name);

  // Verify all expected work channels are in results (positive cases)
  for (const workName of workChannelNames) {
    TestValidator.predicate(`Channel with 'work' in name should be included: ${workName}`)(
      resultChannelNames.some(resultName => 
        resultName.toLowerCase().includes("work")
      )
    );
  }

  // Verify no non-work channels are in results (negative cases)
  for (const nonWorkName of nonWorkChannelNames) {
    TestValidator.predicate(`Channel without 'work' in name should be excluded: ${nonWorkName}`)(
      !resultChannelNames.some(resultName => 
        resultName.toLowerCase().includes("work")
      )
    );
  }

  // Verify all search results contain "work" in the name (case-insensitive)
  for (const channel of searchResults.data) {
    TestValidator.predicate(`All search results must contain 'work' in name (case-insensitive): ${channel.name}`)(
      channel.name.toLowerCase().includes("work")
    );
  }

  // Verify the number of results matches the number of channels with "work" in name
  TestValidator.equals("Number of search results should match work channels count")(
    searchResults.data.length
  )(workChannelNames.length);
}

