/**
 * Test retrieving details of an existing channel by its ID.
 *
 * This test validates the functionality of retrieving a specific channel's details
 * through the GET /todoList/todoListChannels/{id} endpoint. The test follows a
 * complete user journey that includes user registration, authentication, channel
 * creation, and finally retrieving the channel details.
 *
 * The test verifies that:
 * 1. A user can be successfully registered
 * 2. The user can authenticate and obtain a valid JWT token
 * 3. The user can create a new channel
 * 4. The created channel can be retrieved by its ID
 * 5. The returned channel information contains all expected fields (name, description, 
 *    color, icon, timestamps) but excludes the owner ID for privacy protection
 *
 * The test uses the channel's unique identifier (ID) to retrieve its details
 * and verifies the response matches the expected ITodoListChannel.ISummary
 * structure. This ensures the endpoint properly handles authenticated requests
 * and returns the correct data format without exposing sensitive ownership
 * information.
 */
export async function test_api_get_channel_details_success(
  connection: api.IConnection
) {
  // Create a random email for user registration
  const email = typia.random<string & tags.Format<"email">>();
  const password = "password123";

  // Step 1: Register a new user
  // Create a user account that can create and retrieve channels.
  // This is required as a prerequisite for authentication.
  const user: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ICreate
    }
  );
  typia.assert(user);

  // Step 2: Authenticate the created user
  // Log in with the created user credentials to obtain a JWT token
  // for accessing protected endpoints. This establishes the authentication
  // context for subsequent API calls.
  const authenticatedUser: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ILogin
    }
  );
  typia.assert(authenticatedUser);

  // Step 3: Create a channel for testing
  // Create a new channel to establish test data for the retrieval test.
  // This provides a valid channel ID that can be used in the GET request.
  const channel: ITodoListChannel = await api.functional.todoList.todoListChannels.create(
    connection,
    {
      body: {
        name: RandomGenerator.paragraph()(),
        description: RandomGenerator.content()()(),
        color: "#FF5733",
        icon: "checklist"
      } satisfies ITodoListChannel.ICreate
    }
  );
  typia.assert(channel);

  // Step 4: Validate the authentication token
  // Verify that the authentication token is still valid before making
  // API calls to protected endpoints. This ensures our authentication
  // context remains active and valid for the channel retrieval operation.
  // Get the token from the connection headers with proper null checks
  const authorization = connection.headers?.Authorization;
  const token = typeof authorization === "string" ? authorization.replace("Bearer ", "") : "";

  const validationResponse: ITokenValidationResponse = await api.functional.auth.user.validate.validateToken(
    connection,
    {
      body: {
        token
      } satisfies ITokenValidationRequest
    }
  );
  typia.assert(validationResponse);
  TestValidator.equals("token should be valid")(validationResponse.valid)(true);

  // Step 5: Retrieve the channel details using the created channel's ID
  // Test the main functionality: getting channel details by ID.
  // This verifies that the endpoint correctly returns channel information
  // for an existing channel.
  const channelDetails: ITodoListChannel.ISummary = await api.functional.todoList.todoListChannels.at(
    connection,
    {
      id: channel.id
    }
  );
  typia.assert(channelDetails);

  // Verify the response contains all expected channel information
  // except the owner ID for privacy protection as specified in the API.
  // The summary response should not include todo_list_user_id.
  TestValidator.equals("channel ID matches")(channelDetails.id)(channel.id);
  TestValidator.equals("channel name matches")(channelDetails.name)(channel.name);
  TestValidator.equals("channel description matches")(channelDetails.description)(channel.description);
  TestValidator.equals("channel color matches")(channelDetails.color)(channel.color);
  TestValidator.equals("channel icon matches")(channelDetails.icon)(channel.icon);
  TestValidator.equals("channel code matches")(channelDetails.code)(channel.code);
  TestValidator.equals("channel created_at matches")(channelDetails.created_at)(channel.created_at);
}

