/**
 * Test successful update of an existing todo list configuration.
 *
 * This test validates the complete workflow for updating a todo list configuration:
 * 1. Register a new admin user to establish authentication context
 * 2. Login with the registered user to obtain authentication tokens 
 * 3. Create a new configuration to have a target for update
 * 4. Update the configuration with new values
 * 5. Verify the response reflects the changes while maintaining the original creation timestamp
 *
 * The test specifically validates that:
 * - Admin users can successfully update configuration settings
 * - The configuration value and description are properly updated  
 * - The configuration name remains unchanged (immutable after creation)
 * - The created_at timestamp remains unchanged (creation time is immutable)
 * - The updated_at timestamp is properly updated to reflect the modification time
 */
export async function test_api_todo_list_configuration_update_success(
  connection: api.IConnection,
) {
  // 1. Create an admin user for authentication
  // Generate unique email and password for the test user
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = RandomGenerator.alphabets(8) + "1@"; // Create password that meets basic security requirements

  // Register the admin user  
  const registeredUser: ITodoListUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email,
      password,
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(registeredUser);

  // Verify the user was successfully registered
  TestValidator.equals("User should be registered successfully")(
    registeredUser.id.length > 0
  )(true);

  // 2. Login to obtain authentication token
  const loggedInUser: ITodoListUser = await api.functional.auth.user.login.signIn(connection, {
    body: {
      email,
      password,
    } satisfies ITodoListUser.ILogin,
  });
  typia.assert(loggedInUser);

  // Verify successful login
  TestValidator.equals("User should be authenticated")(
    loggedInUser.id
  )(registeredUser.id);

  // 3. Create a test configuration to update
  // Generate unique configuration name to avoid conflicts
  const configurationName = `test_config_${RandomGenerator.alphabets(5).toLowerCase()}`;
  const configurationValue = "initial_value"; 
  const configurationDescription = "Initial description for test configuration";

  // Create the test configuration
  const createdConfiguration: ITodoListConfiguration = await api.functional.todoList.todoListConfigurations.create(connection, {
    body: {
      name: configurationName,
      value: configurationValue,
      description: configurationDescription,
    } satisfies ITodoListConfiguration.ICreate,
  });
  typia.assert(createdConfiguration);

  // Store creation timestamp for later comparison
  const createdAt = createdConfiguration.created_at;

  // Verify the configuration was created successfully
  TestValidator.equals("Configuration should be created with correct values")(
    createdConfiguration.name
  )(configurationName);
  TestValidator.equals("Configuration should have correct initial value")(
    createdConfiguration.value
  )(configurationValue);
  TestValidator.equals("Configuration should have creation timestamp")(
    createdConfiguration.created_at.length > 0
  )(true);

  // 4. Update the configuration with new values
  const updatedValue = "updated_value";
  const updatedDescription = "Updated description for test configuration";

  // Perform the update operation
  const updatedConfiguration: ITodoListConfiguration = await api.functional.todoList.todoListConfigurations.update(connection, {
    id: createdConfiguration.id,
    body: {
      value: updatedValue,
      description: updatedDescription,
    } satisfies ITodoListConfiguration.IUpdate,
  });
  typia.assert(updatedConfiguration);

  // 5. Validate the updated configuration  
  // Verify that the configuration name remains unchanged (business rule)
  TestValidator.equals("Configuration name should remain unchanged - names are immutable after creation")(
    updatedConfiguration.name
  )(configurationName);

  // Verify that the value was updated successfully
  TestValidator.equals("Configuration value should be updated to new value")(
    updatedConfiguration.value
  )(updatedValue);

  // Verify that the description was updated successfully
  TestValidator.equals("Configuration description should be updated to new description")(
    updatedConfiguration.description
  )(updatedDescription);

  // Verify that created_at timestamp remains unchanged (business rule)
  TestValidator.equals("Creation timestamp should remain unchanged - creation time is immutable")(
    updatedConfiguration.created_at
  )(createdAt);

  // Verify that updated_at timestamp has been updated and is later than creation time
  TestValidator.predicate("Updated timestamp should be different from creation timestamp and reflect the modification time")(
    new Date(updatedConfiguration.updated_at) > new Date(createdAt)
  );
};

