import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListChannel";
import type { ITodoListChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListChannels";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

/**
 * Test successfully updating a channel that the user owns.
 * 
 * This test verifies that a user can successfully update a channel they own
 * through the PUT /todoList/channels/{id} endpoint. The test follows a complete
 * user journey from registration to channel update verification.
 * 
 * The test workflow:
 * 1. Register a new user with a randomly generated email and password
 * 2. Login as the registered user to authenticate subsequent requests
 * 3. Create a new channel with initial properties (name, description, color, icon)
 * 4. Store the channel's initial state, particularly the updated_at timestamp
 * 5. Update the channel with new values for name, description, color, and icon
 * 6. Verify the response contains the updated values
 * 7. Verify the updated_at timestamp has been updated (is newer than initial)
 * 
 * This test ensures that:
 * - The channel update operation returns a 200 OK response
 * - The updated channel properties match the requested changes
 * - The updated_at timestamp is properly updated to reflect the modification time
 * - The authentication system correctly associates the channel with the user
 * 
 * The test uses realistic data and follows the complete business workflow
 * that a real user would experience when updating a channel.
 */
export async function test_api_channel_update_success_owned_channel(connection: api.IConnection) {
  // 1. Register a new user
  const email = typia.random<string & tags.Format<"email">>();
  const password = "password123";

  const registeredUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ICreate
  });
  typia.assert(registeredUser);

  // 2. Login as the registered user
  const loggedInUser = await api.functional.auth.user.login.signIn(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ILogin
  });
  typia.assert(loggedInUser);

  // 3. Create a new channel
  const initialChannel = await api.functional.todoList.todoListChannels.create(connection, {
    body: {
      name: "Initial Channel Name",
      description: "Initial channel description",
      color: "#FF5733",
      icon: "checklist"
    } satisfies ITodoListChannel.ICreate
  });
  typia.assert(initialChannel);

  // Store the initial updated_at timestamp
  const initialUpdatedAt = initialChannel.updated_at;

  // 4. Update the channel with new values
  const updatedChannel = await api.functional.todoList.channels.update(connection, {
    id: initialChannel.id,
    body: {
      name: "Updated Channel Name",
      description: "Updated channel description",
      color: "#33A1FF",
      icon: "calendar",
      updated_at: new Date().toISOString()
    } satisfies ITodoListChannels.IUpdate
  });
  typia.assert(updatedChannel);

  // 5. Verify the updated values match what we sent
  TestValidator.equals("channel name should be updated")(updatedChannel.name)("Updated Channel Name");
  TestValidator.equals("channel description should be updated")(updatedChannel.description)("Updated channel description");
  TestValidator.equals("channel color should be updated")(updatedChannel.color)("#33A1FF");
  TestValidator.equals("channel icon should be updated")(updatedChannel.icon)("calendar");

  // 6. Verify that updated_at timestamp has changed and is newer
  const updatedUpdatedAt = updatedChannel.updated_at;
  TestValidator.predicate("updated_at should be different from initial")(
    updatedUpdatedAt !== initialUpdatedAt
  );

  // Parse dates and verify the update time is after the initial time
  const initialDate = new Date(initialUpdatedAt);
  const updatedDate = new Date(updatedUpdatedAt);
  TestValidator.predicate("updated_at should be after initial updated_at")(
    updatedDate > initialDate
  );
}

