/**
 * Test user login functionality.
 *
 * This test verifies the complete user authentication workflow:
 * 1. Register a new user with valid credentials
 * 2. Successfully authenticate with correct credentials
 * 3. Verify the authentication updates user timestamps
 * 4. Test authentication failure with incorrect password
 * 
 * The test ensures that:
 * - The system correctly authenticates users with valid credentials
 * - Successful login updates the user's last seen timestamp
 * - Invalid credentials are rejected with appropriate error
 * - The system protects against authentication bypass
 * - User data remains consistent across authentication
 * 
 * Note: This test intentionally does not check for non-existent users
 * to prevent user enumeration attacks that could compromise security.
 */
export async function test_api_auth_user_login_signIn(connection: api.IConnection) {
  // Step 1: Register a new user
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = `${RandomGenerator.alphaNumeric(8)}@${Math.floor(Math.random() * 9) + 1}`;

  const registration: ITodoListUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(registration);

  // Step 2: Test successful login with correct credentials
  const loginSuccess: ITodoListUser = await api.functional.auth.user.login.signIn(connection, {
    body: {
      email,
      password
    } satisfies ITodoListUser.ILogin,
  });
  typia.assert(loginSuccess);

  // Verify login response matches registration data
  TestValidator.equals("user ID unchanged")(loginSuccess.id)(registration.id);
  TestValidator.equals("created_at unchanged")(loginSuccess.created_at)(registration.created_at);

  // Verify updated_at has been updated (login should update this)
  const loginUpdated = new Date(loginSuccess.updated_at);
  const registrationUpdated = new Date(registration.updated_at);
  TestValidator.predicate("updated_at updated on login")(
    loginUpdated.getTime() > registrationUpdated.getTime()
  );

  // Verify the time difference is reasonable (within a short timeframe)
  const timeDifference = loginUpdated.getTime() - registrationUpdated.getTime();
  TestValidator.predicate("updated_at updated reasonably")(timeDifference < 30000); // Less than 30 seconds

  // Step 3: Test login with incorrect password
  // The system should reject authentication attempts with invalid passwords
  await TestValidator.error("login fails with incorrect password")(() =>
    api.functional.auth.user.login.signIn(connection, {
      body: {
        email,
        password: "WrongPassword123!"
      } satisfies ITodoListUser.ILogin,
    })
  );
}

