/**
 * Test searching for users with valid search criteria.
 * 
 * This test validates the user search functionality with various search criteria 
 * including partial name and email matching. It follows a complete user journey:
 * 
 * 1. Register and authenticate as a primary user to authorize search operations
 * 2. Register multiple additional users to create search data
 * 3. Perform searches with different criteria (name, email)
 * 4. Validate paginated results contain correct user summaries
 * 5. Verify search results match expected criteria
 * 6. Check pagination metadata is accurate
 * 
 * The test ensures the system returns a properly paginated list of user summaries 
 * that match search criteria with appropriate metadata. It validates both single 
 * and multiple result scenarios, and confirms the search functionality works 
 * with partial string matching.
 */
export async function test_api_user_search_valid_criteria(
  connection: api.IConnection,
) {
  // 1. Register and authenticate primary user
  const primaryUserEmail = typia.random<string & tags.Format<"email">>();
  const primaryUser = await api.functional.auth.user.register.signUp(connection, {
    body: {
      email: primaryUserEmail,
      password: "password123"
    } satisfies ITodoListUser.ICreate,
  });
  typia.assert(primaryUser);

  // Authenticate as primary user for authorized search operations
  const authenticatedUser = await api.functional.auth.user.login.signIn(connection, {
    body: {
      email: primaryUserEmail,
      password: "password123"
    } satisfies ITodoListUser.ILogin,
  });
  typia.assert(authenticatedUser);

  // 2. Register additional users for search data
  const userNames = ArrayUtil.repeat(3)(() => RandomGenerator.name());
  const userEmails = ArrayUtil.repeat(3)(() => 
    typia.random<string & tags.Format<"email">>()
  );

  const createdUsers: ITodoListUser.ISummary[] = [];
  for (let i = 0; i < 3; i++) {
    const user = await api.functional.auth.user.register.signUp(connection, {
      body: {
        email: userEmails[i],
        password: "password123"
      } satisfies ITodoListUser.ICreate,
    });
    typia.assert(user);

    createdUsers.push({
      id: user.id,
      created_at: user.created_at
    });
  }

  // 3. Search for users with partial name match
  const partialName = userNames[0].substring(0, 3);
  const searchByName = await api.functional.todoList.users.search(connection, {
    body: {
      search: partialName,
      page: 1,
      limit: 10,
      sort_by: "created_at",
      sort_order: "asc"
    } satisfies ITodoListUser.IRequest,
  });
  typia.assert(searchByName);

  // Validate results contain users matching the name criteria
  TestValidator.equals(
    `search by name partial match '${partialName}'`
  )(searchByName.data.length >= 1)(true);

  // At least the user with matching name should be found
  const foundByName = searchByName.data.some(u => 
    createdUsers.some(usr => usr.id === u.id)
  );
  TestValidator.equals("found user by name partial match")(foundByName)(true);

  // 4. Search for users with partial email match
  const targetEmail = userEmails[1];
  const partialDomain = targetEmail.split('@')[1].substring(0, 5);

  const searchByEmail = await api.functional.todoList.users.search(connection, {
    body: {
      search: partialDomain,
      page: 1,
      limit: 10
    } satisfies ITodoListUser.IRequest,
  });
  typia.assert(searchByEmail);

  // Validate results contain users with email matching the domain
  TestValidator.equals(
    `search by email domain partial match '${partialDomain}'`
  )(searchByEmail.data.length >= 1)(true);

  // Check that pagination metadata is present and reasonable
  TestValidator.equals("pagination current page")(
    searchByEmail.pagination.current
  )(1);
  TestValidator.equals("pagination limit")(
    searchByEmail.pagination.limit
  )(10);
  TestValidator.equals("pagination records at least 1")(
    searchByEmail.pagination.records >= 1
  )(true);

  // 5. Test with multiple search criteria
  const searchMultiple = await api.functional.todoList.users.search(connection, {
    body: {
      page: 1,
      limit: 50,
      sort_by: "created_at",
      sort_order: "desc"
    } satisfies ITodoListUser.IRequest,
  });
  typia.assert(searchMultiple);

  // Verify all created users are included when no filter is applied
  // (assuming no other users exist in the system)
  TestValidator.equals(
    "all users returned with no search criteria"
  )(searchMultiple.data.length >= 3)(true);
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";
import type { IPageITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageITodoListUser";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Test searching for users with non-existent criteria to verify empty results handling.
 *
 * This test validates that the system properly handles search requests for users
 * with criteria that do not match any existing users. The test follows a complete
 * user journey: user registration, authentication, and search with non-matching
 * criteria. The expected behavior is to return a successful response with an
 * empty items array and pagination metadata indicating zero total results.
 *
 * The test verifies both the API contract (response structure) and business
 * logic (empty results for non-existent criteria). It ensures the system
 * doesn't expose user enumeration vulnerabilities by returning consistent
 * responses for both valid and invalid search criteria, which is an important
 * security consideration.
 *
 * Step-by-step process:
 * 1. Generate unique credentials for test user registration
 * 2. Register a new user account
 * 3. Authenticate the newly registered user
 * 4. Search for users with a randomly generated name that doesn't exist
 * 5. Verify the response contains empty data array and zero pagination counts
 */
export async function test_api_user_search_nonexistent_criteria(connection: api.IConnection) {
  // Generate unique email and password for the test user
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = typia.random<string & tags.MinLength<8>>();

  // Register a new user to authenticate the search request
  const registration: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ICreate
    }
  );
  typia.assert(registration);

  // Authenticate the user to authorize the search request
  const login: ITodoListUser = await api.functional.auth.user.login.signIn(
    connection,
    {
      body: {
        email,
        password
      } satisfies ITodoListUser.ILogin
    }
  );
  typia.assert(login);

  // Generate a unique search term that won't match any existing users
  const nonExistentName: string = typia.random<string & tags.MaxLength<50>>();

  // Search for users with non-existent criteria
  const searchResult: IPageITodoListUser.ISummary = await api.functional.todoList.users.search(
    connection,
    {
      body: {
        search: nonExistentName,
        page: 1,
        limit: 10,
        sort_by: "created_at",
        sort_order: "desc"
      } satisfies ITodoListUser.IRequest
    }
  );
  typia.assert(searchResult);

  // Verify the response contains empty results for non-existent criteria
  TestValidator.equals("Search should return empty data array when no users match")
    (searchResult.data)
    ([]);

  TestValidator.equals("Pagination should show zero records when no users match")
    (searchResult.pagination.records)
    (0);

  TestValidator.equals("Pagination should show one page even with zero records")
    (searchResult.pagination.pages)
    (1);

  TestValidator.equals("Current page should match requested page")
    (searchResult.pagination.current)
    (1);

  TestValidator.equals("Limit should match requested limit")
    (searchResult.pagination.limit)
    (10);
}

