import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

/**
 * Test retrieving a user profile immediately after registration.
 *
 * This test verifies that user profile data is consistent and properly
 * initialized immediately after registration. The test registers a new
 * user and immediately retrieves their profile without logging in again,
 * ensuring data consistency across the registration and retrieval operations.
 *
 * The test validates that:
 * 1. User registration returns a valid user object with properly initialized timestamps
 * 2. The directly retrieved profile matches the registration response data exactly
 * 3. created_at and updated_at timestamps are identical immediately after registration
 *    (since no updates have occurred yet)
 * 4. All timestamps are in proper ISO format
 * 5. No data is lost or altered between registration and immediate retrieval
 *
 * This immediate consistency check ensures the system properly persists
 * user data and maintains data integrity from the moment of creation.
 * It also verifies the basic read-after-write consistency of the user
 * profile system.
 */
export async function test_api_user_profile_retrieval_immediately_after_registration(
  connection: api.IConnection,
) {
  // Generate random email for user registration
  const email: string = typia.random<string & tags.Format<"email">>();
  const password: string = "P@ssw0rd123!";

  // Step 1: Register a new user
  const registeredUser: ITodoListUser = await api.functional.auth.user.register.signUp(
    connection,
    {
      body: {
        email,
        password,
      } satisfies ITodoListUser.ICreate,
    }
  );
  typia.assert(registeredUser);

  // Validate registration response
  TestValidator.equals("User should have an ID")(registeredUser.id.length > 0)(true);
  TestValidator.equals("created_at should be present")(registeredUser.created_at !== undefined)(true);
  TestValidator.equals("updated_at should be present")(registeredUser.updated_at !== undefined)(true);

  // Step 2: Immediately retrieve the user's profile using the registration response ID
  // Note: We're not logging in again; the registration response contains the user data
  const retrievedUser: ITodoListUser = await api.functional.todoList.users.at(
    connection,
    {
      userId: registeredUser.id,
    }
  );
  typia.assert(retrievedUser);

  // Validate that retrieved profile matches registration data exactly
  TestValidator.equals("User IDs should match")(retrievedUser.id)(registeredUser.id);
  TestValidator.equals("Emails should match")(retrievedUser.id)(registeredUser.id);
  TestValidator.equals("created_at should match")(retrievedUser.created_at)(registeredUser.created_at);
  TestValidator.equals("updated_at should match")(retrievedUser.updated_at)(registeredUser.updated_at);

  // Validate that created_at and updated_at are identical immediately after registration
  TestValidator.equals("created_at and updated_at should be equal initially")(retrievedUser.created_at)(retrievedUser.updated_at);

  // Validate timestamp formats
  TestValidator.predicate("created_at should be valid ISO date")(!isNaN(Date.parse(retrievedUser.created_at)));
  TestValidator.predicate("updated_at should be valid ISO date")(!isNaN(Date.parse(retrievedUser.updated_at)));
}
