import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendTenants } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendTenants";

/**
 * Validate error handling when archiving a non-existent tenant.
 *
 * This test attempts to archive (set archived=true) a tenant using a random UUID that does not correspond to any existing tenant entity in the system.
 * Expected result: The API should throw a 'not found' error (likely HttpError 404) when given an invalid tenant id.
 *
 * Step-by-step:
 * 1. Generate a random UUID (not matching any created tenant).
 * 2. Attempt to archive the tenant using the eraseById API.
 * 3. Assert that an error is thrown (do not validate error message or type, just that an error occurs).
 */
export async function test_api_aiMallBackend_tenants_test_archive_tenant_nonexistent(
  connection: api.IConnection,
) {
  // 1. Generate a random UUID that doesn't correspond to any tenant
  const nonexistentId = typia.random<string & tags.Format<"uuid">();

  // 2. Attempt to archive using the archive (erase) API
  await TestValidator.error("archive non-existent tenant should fail")(
    async () => {
      await api.functional.aiMallBackend.tenants.erase(connection, {
        id: nonexistentId,
      });
    }
  );
}

