import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendSections";

/**
 * Validate error handling for creating a section with an invalid channel association (referential integrity enforcement)
 *
 * This test ensures that the API correctly rejects the creation of a new ai_mall_backend_sections record when the supplied
 * channel_id does not correspond to an existing or enabled ai_mall_backend_channels entry. This maintains foreign key integrity
 * and prevents business logic errors from orphaned or invalid section references. The system must respond with an error rather
 * than creating the section.
 *
 * Steps:
 * 1. Generate a random UUID to simulate a non-existent channel_id (not present in channels table)
 * 2. Construct a valid IAiMallBackendSections.ICreate object, but set the channel_id to this invalid value
 * 3. Attempt to create the section via the create API
 * 4. Assert that the API responds with an error (e.g., referential integrity violation, invalid foreign key, or business logic error)
 * 5. Verify that no section is actually created and that the error is properly formatted according to API contract
 */
export async function test_api_aiMallBackend_test_create_section_with_invalid_channel_id(
  connection: api.IConnection,
) {
  // 1. Generate a random non-existent/deleted channel_id
  const invalidChannelId: string = typia.random<string & tags.Format<"uuid">>();

  // 2. Construct section creation data with invalid channel_id
  const sectionData: IAiMallBackendSections.ICreate = {
    channel_id: invalidChannelId,
    code: "invalid_test_section",
    name: "Section with invalid channel association",
    enabled: true,
  };

  // 3. Attempt to create section and 4. Assert on error
  await TestValidator.error("should fail with invalid channel_id")(() =>
    api.functional.aiMallBackend.sections.create(connection, {
      body: sectionData,
    })
  );
}

