import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendConfigurations } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendConfigurations";

/**
 * Validate failure when creating a configuration with an invalid channel reference.
 *
 * This test checks that the backend enforces foreign key constraints by rejecting
 * a configuration creation request containing a random/nonexistent channel_id.
 *
 * Steps:
 * 1. Construct a valid configuration creation body, but set channel_id to a random UUID
 *    that does not exist as a channel in the system.
 * 2. Attempt to create the configuration using the exposed POST endpoint.
 * 3. Assert that the request fails, raising an error (such as foreign key constraint failure),
 *    and that no configuration is created.
 */
export async function test_api_aiMallBackend_test_create_configuration_with_invalid_channel_id(
  connection: api.IConnection,
) {
  // 1. Prepare a config creation body with a guaranteed-nonexistent channel_id
  const body: IAiMallBackendConfigurations.ICreate = {
    channel_id: typia.random<string & tags.Format<"uuid">>(),
    name: `invalid-channel-config-${RandomGenerator.alphaNumeric(8)}`,
    value: RandomGenerator.alphaNumeric(16),
    description: "Testing creation with a bogus channel_id: should fail."
  };

  // 2. Attempt to create the configuration (should error)
  await TestValidator.error("Should reject creation with invalid channel_id")(async () => {
    await api.functional.aiMallBackend.configurations.create(connection, {
      body
    });
  });
}

