import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendTenants } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendTenants";

/**
 * Validates unique constraint enforcement when creating aiMallBackend tenants.
 *
 * This test covers the scenario where an admin attempts to create a new tenant with a code or name that already exists in the system. According to the business rules, both code and name must be globally unique among tenants. Attempting to create a duplicate (by code, name, or both) must result in a rejected API call with a unique constraint violation.
 *
 * Steps:
 * 1. Create the first tenant with a specific code and name (baseline for uniqueness).
 * 2. Attempt to create a different tenant using the same code but a new name. Verify that the API call fails.
 * 3. Attempt to create a different tenant using the same name but a new code. Verify that the API call fails.
 * 4. Attempt to create a different tenant using both the same code and same name. Verify that the API call fails.
 *
 * This ensures that both the code and name uniqueness constraints are strictly enforced by the backend API, providing robustness for tenant identity management.
 */
export async function test_api_aiMallBackend_tenants_test_create_tenant_with_duplicate_code_or_name(
  connection: api.IConnection,
) {
  // Step 1: Create the initial tenant.
  const baseCode = `code_${RandomGenerator.alphaNumeric(8)}`;
  const baseName = `Tenant ${RandomGenerator.alphabets(6)}`;
  const initialTenant = await api.functional.aiMallBackend.tenants.create(connection, {
    body: {
      code: baseCode,
      name: baseName,
      archived: false,
    } satisfies IAiMallBackendTenants.ICreate,
  });
  typia.assert(initialTenant);

  // Step 2: Try to create new tenant with duplicate code and new name.
  await TestValidator.error("duplicate code fails")(
    async () => {
      await api.functional.aiMallBackend.tenants.create(connection, {
        body: {
          code: baseCode, // duplicate code
          name: `Tenant ${RandomGenerator.alphabets(6)}`,
          archived: false,
        } satisfies IAiMallBackendTenants.ICreate,
      });
    },
  );

  // Step 3: Try to create new tenant with new code and duplicate name.
  await TestValidator.error("duplicate name fails")(
    async () => {
      await api.functional.aiMallBackend.tenants.create(connection, {
        body: {
          code: `code_${RandomGenerator.alphaNumeric(8)}`,
          name: baseName, // duplicate name
          archived: false,
        } satisfies IAiMallBackendTenants.ICreate,
      });
    },
  );

  // Step 4: Try to create new tenant with duplicate code and duplicate name.
  await TestValidator.error("duplicate code and name fails")(
    async () => {
      await api.functional.aiMallBackend.tenants.create(connection, {
        body: {
          code: baseCode, // duplicate code
          name: baseName, // duplicate name
          archived: true,
        } satisfies IAiMallBackendTenants.ICreate,
      });
    },
  );
}

