/**
 * Validate that retrieving an API gateway by id after soft deletion returns an error.
 *
 * This test ensures correct exclusion of soft-deleted records from API gateway queries.
 *
 * Business context:
 * - API gateways are soft-deletable  i.e., deleted by setting deleted_at, not physical removal.
 * - Retrieving a soft-deleted gateway should yield not-found (404) or gone (410) response by business rule, to prevent disclosure or access of deactivated records.
 *
 * Steps:
 * 1. Create a channel (required for associating the gateway).
 * 2. Create an API gateway associated with the newly created channel.
 * 3. Soft-delete (erase) the created gateway by id.
 * 4. Attempt to retrieve (GET) the same gateway by id.
 * 5. Verify that an error (not found or gone) is thrown, i.e., system does not return the record.
 */
export async function test_api_aiMallBackend_apiGateways_test_get_api_gateway_by_soft_deleted_id(
  connection: api.IConnection,
) {
  // 1. Create a channel to associate with the API gateway
  const channel = await api.functional.aiMallBackend.channels.create(connection, {
    body: {
      tenant_id: typia.random<string & tags.Format<"uuid">>(),
      code: RandomGenerator.alphaNumeric(8),
      name: RandomGenerator.name(),
      enabled: true,
    } satisfies IAiMallBackendChannels.ICreate,
  });
  typia.assert(channel);

  // 2. Create the API gateway
  const gateway = await api.functional.aiMallBackend.apiGateways.create(connection, {
    body: {
      channel_id: channel.id,
      route_base: "/api/v1" + RandomGenerator.alphaNumeric(4),
      domain: `${RandomGenerator.alphaNumeric(8)}.example.com`,
      enabled: true,
    } satisfies IAiMallBackendApiGateways.ICreate,
  });
  typia.assert(gateway);

  // 3. Soft-delete the API gateway (set deleted_at)
  const erased = await api.functional.aiMallBackend.apiGateways.erase(connection, { id: gateway.id });
  typia.assert(erased);
  TestValidator.predicate("deleted_at is set after erase")(!!erased.deleted_at);

  // 4. Attempt to GET by id (should error: not found/gone)
  await TestValidator.error("should not find deleted API gateway")(() =>
    api.functional.aiMallBackend.apiGateways.at(connection, { id: gateway.id })
  );
}

