    name: RandomGenerator.name(),
    archived: true,
  } satisfies IAiMallBackendTenants.ICreate;

  const created = await api.functional.aiMallBackend.tenants.create(connection, { body: input });
  typia.assert(created);

  // 2. Call the archive endpoint again
  const archivedAgain = await api.functional.aiMallBackend.tenants.erase(connection, { id: created.id });
  typia.assert(archivedAgain);

  // 3. Check id, archived and immutability of important fields
  TestValidator.equals("Entity id remains the same")(archivedAgain.id)(created.id);
  TestValidator.equals("Archived flag is true")(archivedAgain.archived)(true);
  TestValidator.equals("Tenant code is unchanged")(archivedAgain.code)(created.code);
